import { useState, useEffect } from "react";
import axios from "axios";
import Layout from "../layout";
import Slider from "../components/slider";

function Favorites() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .post("/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="w-9/12 pt-10 m-auto">
        <h3 className="font-medium text-2xl text-gray-700 mb-5">Favorites</h3>
        <div className="pt-10 pb-2">
          <p>Items added to your Favourites will be saved here.</p>
        </div>
        <Slider products={products} heading="Discover Your Next Favorites" />
      </div>
    </Layout>
  );
}

export default Favorites;
