import { useState } from "react";

const Checkbox = ({ label, value, type }) => (
  <div>
    <input
      type="checkbox"
      className="w-4 h-4 my-1.5"
      value={value}
      onChange={(_) => (type === "price" ? setPrice(value) : setOffer(value))}
    />
    <label className="text-sm ml-2">{label}</label>
  </div>
);

const ColorOption = ({ color }) => (
  <div className="inline-block w-5/12 text-center mt-5 mr-2 hover:brightness-125 hover:cursor-pointer">
    <div
      className={`w-5 h-5 bg-${color}-400 rounded-xl m-auto`}
      onCLick={(_) => setColor(color)}
    ></div>
    <span className="font-medium text-sm">{color}</span>
  </div>
);

const SizeOption = ({ size }) => (
  <div
    className="w-2/6 inline-block hover:border-gray-700 text-sm text-center border border-gray-200 rounded-md py-1.5 m-1 cursor-pointer"
    onCLick={(_) => setSize(size)}
  >
    {size}
  </div>
);

function Filter() {
  const priceRanges = [
    { label: "Below $1000", value: "<1000" },
    { label: "$1000 - $2000", value: "1000-2000" },
    { label: "$2000 - $3000", value: "2000-3000" },
    { label: "Above $4000", value: ">4000" },
  ];
  const discountOffers = ["10", "20", "30"];
  const colors = [
    "blue",
    "gray",
    "green",
    "indigo",
    "pink",
    "purple",
    "red",
    "yellow",
  ];
  const sizes = [
    5, 5.5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 9.5, 10, 10.5, 11, 11.5, 12,
  ];
  const [price, setPrice] = useState();
  const [offer, setOffer] = useState();
  const [color, setColor] = useState();
  const [size, setSize] = useState();

  return (
    <>
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Price</h4>
      {priceRanges.map((range, index) => (
        <Checkbox
          key={index}
          label={range.label}
          value={range.value}
          type="price"
        />
      ))}
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Offers</h4>
      {discountOffers.map((discount, index) => (
        <Checkbox
          key={index}
          label={`${discount}% above`}
          value={discount}
          type="offer"
        />
      ))}
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700">Color</h4>
      <div className="w-9/12 mt-2">
        {colors.map((color, index) => (
          <ColorOption key={index} color={color} />
        ))}
      </div>
      <hr className="h-px my-4 bg-gray-200" />
      <h4 className="font-medium text-lg text-gray-700 mb-4">Size</h4>
      <div className="flex flex-wrap">
        {sizes.map((size, index) => (
          <SizeOption key={index} size={size} />
        ))}
      </div>
    </>
  );
}

export default Filter;
