import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import { getProducts } from "../server";

function Cart() {
  const [products, setProducts] = useState([]);
  const position = {
    container: "flex flex-col md:flex-row gap-16 w-9/12 m-auto",
    leftwrapper: "md:w-3/5",
    rightwrapper: "md:w-2/5",
    wrapper: "flex my-5",
  };
  const design = {
    heading: "font-medium text-xl text-gray-700",
  };

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    cart.map((item) => getProducts(item.product, setProducts));
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.leftwrapper}>
          <h2 className={design.heading}>Bag</h2>
          <div className={position.wrapper}></div>
        </div>
        <div className={position.rightwrapper}>
          <h2 className={design.heading}>Summary</h2>
          <div className={position.wrapper}></div>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
