import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "grid grid-cols-3",
    imagewrapper: "p-4 col-span-2",
    productwrapper: "",
    image: "sticky top-0",
  };
  const design = {
    heading: "text-2xl",
    subheading: "text-medium text-gray-500",
    image: "object-contain bg-gray-100 rounded-xl",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.imagewrapper}>
          <img
            className={design.image}
            src={product.image}
            alt={product.slug}
          />
        </div>
        <div class="bg-gray-300 p-4"> This is the smaller column.</div>
      </div>{" "}
    </Layout>
  );
}

export default Product;
