import { useState } from "react";
import DarrowIcon from "../icons/darrow";

function Dropdown(props) {
  const [boolean, setBoolean] = useState(false);
  const position = {
    container: "flex gap-2",
    wrapper: "relative",
    label: "flex gap-2",
    dropdown: "flex flex-col gap-1 absolute w-40 right-0 z-10",
  };
  const design = {
    text: `tracking-wide cursor-pointer ${
      props.textDesign ? props.textDesign : "font-light"
    }`,
    texthover: "hover:text-gray-500",
    dropdown: boolean
      ? "bg-white px-2 pt-5 pb-2 shadow-lg rounded-xl"
      : "hidden",
  };

  return (
    <div className={position.container}>
      <div className={position.wrapper}>
        <div onClick={() => setBoolean(!boolean)} className={position.label}>
          <h2 className={design.text}>{props.text}</h2>
          <div className={design.text}>
            <DarrowIcon />
          </div>
        </div>
        <div className={`${position.dropdown} ${design.dropdown}`}>
          <p
            onClick={(e) => {
              props.onClick(e);
              setBoolean(false);
            }}
            className={`${design.text} ${design.texthover}`}
          >
            {props.subtext[0]}
          </p>
          <p
            onClick={(e) => {
              props.onClick(e);
              setBoolean(false);
            }}
            className={`${design.text} ${design.texthover}`}
          >
            {props.subtext[1]}
          </p>
          <p
            onClick={(e) => {
              props.onClick(e);
              setBoolean(false);
            }}
            className={`${design.text} ${design.texthover}`}
          >
            {props.subtext[2]}
          </p>
        </div>
      </div>
    </div>
  );
}

export default Dropdown;
