function Progressbar(props) {
  const position = {
    container: "max-w-xs mx-auto",
    empty: "overflow-hidden",
    filled: "h-2",
    wrapper: "grid grid-cols-5 mt-4",
    itemWrapper: "flex items-center justify-center",
    item: "hidden sm:inline",
  };
  const design = {
    empty: "bg-gray-200 rounded-full",
    filled: "bg-green-500 rounded-full",
    wrapper: "text-sm font-medium text-gray-500",
  };
  const width = `${(props.index / (props.items.length - 1)) * 100}%`;

  return (
    <div className={position.container}>
      <div className={`${position.empty} ${design.empty}`}>
        <div
          className={`${position.filled} ${design.filled}`}
          style={{ width: width }}
        ></div>
      </div>
      <ol className={`${position.wrapper} ${design.wrapper}`}>
        {props.items.map((item, index) => (
          <li
            key={index}
            className={`${position.itemWrapper} ${
              index <= props.index ? "text-green-600" : ""
            }`}
          >
            <span className={position.wrapper}>{item}</span>
          </li>
        ))}
      </ol>
    </div>
  );
}

export default Progressbar;
