import Card from "./card";
import Cardbig from "./cardbig";
import { shippingDate } from "../helper";

function Grid(props) {
  const position = {
    container:
      "grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-6 gap-4 w-full",
    orderContainer: "grid grid-cols-1 md:grid-cols-2 gap-6",
  };
  const design = {
    text: "text-gray-400",
  };

  if (props.products?.length) {
    return (
      <div className={position.container}>
        {props.products.map((item) => (
          <Card key={item._id} product={item} />
        ))}
      </div>
    );
  } else if (props.orders) {
    return (
      <div className={position.orderContainer}>
        {props.orders.cart.map((item) => (
          <Cardbig
            key={item._id}
            product={item.product}
            cart={item}
            badge={
              props.orders.shipping.status === "Processing"
                ? props.orders.shipping.returnDate
                  ? `Pickup by ${shippingDate(
                      props.orders.shipping.returnDate,
                    )}`
                  : `Delivery by ${shippingDate(props.orders.shipping.date)}`
                : props.orders.shipping.status
            }
            onClick={() => props.onClick(item)}
          />
        ))}
      </div>
    );
  } else {
    return <p className={design.text}>No data found</p>;
  }
}

export default Grid;
