import { useState } from "react";
import Layout from "../layout";

function Checkout() {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <Layout>
      <div className="w-2/4 m-auto pt-10">
        {["Delivery", "Shipping", "Billing", "Payment"].map((item, index) => (
          <div key={index} onClick={() => setActiveIndex(index)}>
            <hr className="h-px my-4 bg-gray-200" />
            <h3
              className={`text-xl font-medium mb-8 ${
                activeIndex === index ? "text-gray-900" : "text-gray-500"
              }`}
            >
              {item}
            </h3>
            {item === "Delivery" ? (
              <Delivery />
            ) : item === "Shipping" ? (
              <Shipping />
            ) : item === "Billing" ? (
              <Billing />
            ) : (
              <Payment />
            )}
          </div>
        ))}
      </div>
    </Layout>
  );
}

function Delivery() {
  const [errors, setErrors] = useState({});
  const [delivery, setDelivery] = useState({
    first_name: "",
    last_name: "",
    address: "",
    email: "",
    phone: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setDelivery((prevState) => ({ ...prevState, [name]: value }));
  }

  function handleSubmit() {
    let newErrors = {};
    Object.keys(delivery).forEach((key) => {
      const value = delivery[key].trim();
      switch (key) {
        case "email":
          if (!value || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
            newErrors[key] = "Please enter a valid email";
          }
          break;
        case "phone":
          if (!value || !/^\d{10}$/.test(value)) {
            newErrors[key] = "Please enter a valid phone number";
          }
          break;
        default:
          if (!value) {
            newErrors[key] = `Please enter your ${key.replace("_", " ")}`;
          }
          break;
      }
    });
    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Proceed with submission
    }
  }
  return (
    <div>
      <h4 className="font-medium">Enter your name and address:</h4>
      <div className="*: *: *:border *: *: *: *:">
        <input
          name="first_name"
          value={delivery.first_name}
          onChange={handleChange}
          className="hover:border-gray-700 block w-full border-gray-300 rounded-md p-4 mt-4
"
          placeholder="First Name"
        />
        {errors.first_name && (
          <span className="text-red-500">{errors.first_name}</span>
        )}{" "}
        <input
          name="last_name"
          value={delivery.last_name}
          onChange={handleChange}
          className="hover:border-gray-700 block w-full border-gray-300 rounded-md p-4 mt-4"
          placeholder="Last Name"
        />
        {errors.last_name && (
          <span className="text-red-500">{errors.last_name}</span>
        )}
        <input
          name="address"
          value={delivery.address}
          onChange={handleChange}
          className="hover:border-gray-700 block w-full border-gray-300 rounded-md p-4 mt-4"
          placeholder="Address"
        />
        {errors.address && (
          <span className="text-red-500">{errors.address}</span>
        )}
      </div>
      <h4 className="font-medium mt-10">What's your contact information?</h4>
      <div className="*:block *:w-full *:border *:border-gray-300 *:rounded-md *:p-4 *:mt-4">
        <input
          name="email"
          value={delivery.email}
          onChange={handleChange}
          className="hover:border-gray-700 block w-full border-gray-300 rounded-md p-4 mt-4"
          placeholder="Email"
        />
        {errors.email && <span className="text-red-500">{errors.email}</span>}
        <input
          name="phone"
          value={delivery.phone}
          onChange={handleChange}
          className="hover:border-gray-700 block w-full border-gray-300 rounded-md p-4 mt-4"
          placeholder="Phone Number"
        />
        {errors.phone && <span className="text-red-500">{errors.phone}</span>}
      </div>
      <div className="flex flex-col gap-2 mt-8 *:border *:border-gray-700 *:rounded-3xl *:py-3 *:font-medium">
        <button onClick={handleSubmit} className="bg-gray-800 text-white">
          Continue
        </button>
      </div>
    </div>
  );
}

function Shipping() {
  return (
    <div>
      <h4>Shipping content goes here</h4>
    </div>
  );
}

function Billing() {
  return (
    <div>
      <h4>Billing content goes here</h4>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h4>Payment content goes here</h4>
    </div>
  );
}

export default Checkout;
