import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Slider from "../components/slider";
import { getProducts } from "../server";

function Favorite() {
  const position = { container: "w-11/12 m-auto *:mt-5 *:mb-20" };
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts({}, setProducts);
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <Heading>Favorite</Heading>
        <Slider products={products} heading="Similar Products" />
      </div>
    </Layout>
  );
}

function Heading(props) {
  const position = "px-2";
  const design = "font-medium text-2xl";
  return <h1 className={`${position} ${design}`}>{props.children}</h1>;
}

export default Favorite;
