import { useEffect, useState } from "react";
import { Checkmark } from "react-checkmark";
import Layout from "../components/layout/layout";
import { createOrder } from "../server";

function Success() {
  const position = {
    container: "flex flex-col gap-4 my-5",
    wrapper: "grid grid-cols-2 gap-10 w-3/4 m-auto py-10",
    separator: "h-px w-1/4 m-auto",
    heading: "pt-10 pb-2",
    subheading: "pb-2",
  };
  const design = {
    heading: "font-medium text-2xl text-center",
    subheading: "font-medium text-xl text-center",
    subsubheading: "font-medium text-lg text-center",
    text: "text-center",
    separator: "bg-gray-200",
  };
  const [order, setOrder] = useState(
    JSON.parse(sessionStorage.getItem("order")) || {},
  );

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    const user = JSON.parse(sessionStorage.getItem("user"));
    const shipping = JSON.parse(sessionStorage.getItem("shipping"));
    const payment = JSON.parse(sessionStorage.getItem("payment"));

    !order._id &&
      createOrder(
        {
          cart: cart.map((item) => item._id),
          user: user._id,
          shipping: shipping._id,
          payment: payment._id,
        },
        setOrder,
      );
  }, [order]);

  return (
    <Layout>
      <div className={position.container}>
        <Checkmark size="10rem" />
        <Heading text="Order placed successfully!" />
        <Subheading text={`Order ID #` + order._id} />
        <hr className={`${position.separator} ${design.separator}`} />
        <h2 className={design.subheading}>Total: ${order.payment.amount}</h2>
        <hr className={`${position.separator} ${design.separator}`} />
        <div className={position.wrapper}>
          <div>
            <h4 className={`${position.subheading} ${design.subheading}`}>
              Your Information
            </h4>
            <p className={design.text}>
              {order.user.fname} {order.user.lname}
            </p>
            <p className={design.text}>{order.user.email}</p>
            <p className={design.text}>{order.user.phone}</p>
          </div>
          <div>
            <h4 className={`${position.subheading} ${design.subheading}`}>
              Payment Information
            </h4>
            <p className={design.text}>{order.payment.mode}</p>
          </div>
          <div>
            <h4 className={`${position.subheading} ${design.subheading}`}>
              Delivery Information
            </h4>
            <p className={design.text}>{order.user.address}</p>
          </div>
          <div>
            <h4 className={`${position.subheading} ${design.subheading}`}>
              Shipping Information
            </h4>
            <p className={design.text}>{order.shipping.date}</p>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Success;

function Subheading(props) {
  const position = "pb-2";
  const design = "font-medium text-lg text-center";

  return <h3 className={`${position} ${design}`}>{props.text}</h3>;
}

function Heading(props) {
  const position = "pt-10 pb-2";
  const design = "font-medium text-2xl text-center";

  return <h2 className={`${position} ${design}`}>{props.text}</h2>;
}

function Text(props) {
  const design = "text-center";

  return <p className={design}>{props.text}</p>;
}

function Separator() {
  const position = "h-px w-1/4 m-auto";
  const design = "bg-gray-200";

  return <hr className={`${position} ${design}`} />;
}
