import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Layout from "../layout";
import HeartIcon from "../icons/heart";
import StarFull from "../icons/starfull";
import StarDry from "../icons/stardry";

function Product() {
  const [product, setProduct] = useState([]);
  const { slug } = useParams();

  useEffect(() => {
    axios
      .post("/product", { slug: slug })
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  }, [slug]);

  return (
    <Layout>
      {product &&
        product.map((prod) => (
          <div
            key={prod._id}
            className="flex mt-10 sm:gap-2 md:gap-10 w-4/5 m-20 *:p-4"
          >
            <div className="w-3/5">
              <img
                className="sticky top-0 w-11/12 m-auto bg-gray-100 px-5 object-contain border border-transparent rounded-xl"
                src={prod.image}
                alt={prod.name}
                style={{ height: "85vh" }}
              />
            </div>
            <div className="w-2/5">
              <h2 className="text-2xl">{prod.name}</h2>
              <p className="text-medium text-gray-500 pt-1">
                {prod.category} Shoes
              </p>
              <p className="font-medium text-lg pt-4">${prod.price}</p>
              <div className="flex justify-between mt-10 mb-2">
                <p className="font-medium">Select Size</p>
                <p className="text-gray-500">Size Guide</p>
              </div>
              {[0, 2.5, 5].map((increment, index) => (
                <div
                  key={index}
                  className="flex gap-1 text-center *:p-2 *:w-1/5 *:border *:border-gray-200 *:rounded-md *:mt-1"
                >
                  <p className="hover:border-gray-700">{5 + increment}</p>
                  <p className="hover:border-gray-700">{5.5 + increment}</p>
                  <p className="hover:border-gray-700">{6 + increment}</p>
                  <p className="hover:border-gray-700">{6.5 + increment}</p>
                  <p className="hover:border-gray-700">{7 + increment}</p>
                </div>
              ))}
              <div className="flex flex-col gap-2 mt-8 *:border *:border-gray-700 *:rounded-3xl *:py-3 *:font-medium">
                <button className="bg-gray-800 text-white">Add to Bag</button>
                <button className="flex gap-1 justify-center">
                  Favorite <HeartIcon />
                </button>
              </div>
              <div className="mt-10">
                <h4 className="font-medium text-lg text-gray-700">Shipping</h4>
                <p className="mt-1 text-gray-600">
                  You'll see our shipping options at checkout
                </p>
                <p className="mt-6">
                  Free Pickup → &nbsp;
                  <span className="text-gray-500">Find a Store</span>
                </p>
                <p className="mt-10 mb-5 text-gray-600">{prod.description}</p>
              </div>
              <div>
                <hr className="h-px my-8 bg-gray-200" />
                <h4 className="font-medium text-lg text-gray-700 mb-5">
                  Shipping & Returns
                </h4>
                <p className="text-gray-600">
                  Free standard shipping on orders $50+ and free 60-day returns
                  for community members.
                </p>
                <hr className="h-px my-8 bg-gray-200" />
                <h4 className="flex justify-between font-medium text-lg text-gray-700 mb-2">
                  <span>Reviews ({prod.review.length})</span>
                  <Rating count={prod.rating} />
                </h4>
                {prod.review.map((rev, index) => (
                  <div key={index} className="*:text-sm pt-3">
                    <p className="font-medium mt-2 mb-2">{rev.name}</p>
                    <p className="text-gray-600">{rev.review}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ))}
    </Layout>
  );
}

function Rating(props) {
  return (
    <div className="flex gap-1 scale-50">
      {[...Array(5)].map((_, i) =>
        i < Math.floor(props.count) ? <StarFull /> : <StarDry />,
      )}
    </div>
  );
}

export default Product;
