import { useEffect, useState } from "react";

function Toast(props) {
  const [message, setMessage] = useState("");

  useEffect(
    (_) => {
      setTimeout(() => setMessage(props.message), 1000);
      setTimeout(() => setMessage(""), 6000);
    },
    [props.message],
  );

  return (
    message && (
      <div
        className={`fixed bg-gray-800 text-white rounded-md py-3 px-5 z-10 top-4 left-1/2 ${
          message ? "opacity-100" : "opactiy-0"
        }`}
        style={{ transform: "translateX(-50%)", transition: "opacity 0.3s" }}
      >
        {message}
      </div>
    )
  );
}

export default Toast;
