import { useState } from "react";
import Input from "../components/input";
import Button from "../components/button";
import Logo from "../icons/logo";
import { validate } from "../helper";

function Register() {
  const [data, setData] = useState({ email: "", password: "" });
  const [err, setErr] = useState({ email: "", password: "" });
  const position = {
    container: "flex flex-col gap-5 w-4/6 md:w-3/6 lg:w-3/6 m-auto py-20",
    button: "*:w-full *:p-3",
  };
  const design = {};

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    console.log(!errr.email && !errr.password && "TETT");
  }

  return (
    <div className={position.container}>
      <Logo />
      <Email heading="Enter your email to join or sign in." />
    </div>
  );
}

export default Register;

function Email(props) {
  const position = {
    heading: "mb-3",
  };
  const heading = {
    heading: "text-3xl text-gray-700",
  };

  return (
    <>
      <h1 className={`${position.heading} ${design.heading}`}>
        {props.heading}
      </h1>
      <div>
        <Input
          name="email"
          value={data.email}
          placeholder="Email"
          onChange={onChange}
          errName={err.email}
        />
      </div>
      <div className={position.button}>
        <Button text="Continue" onClick={onClick} />
      </div>
    </>
  );
}
