import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import Bin from "../icons/bin";
import { getDiscountedPrice } from "../helper";

function Cart() {
  const [cart, setCart] = useState([]);
  const position = {
    container: "flex flex-col md:flex-row gap-16 w-9/12 m-auto",
    leftwrapper: "md:w-3/5",
    rightwrapper: "md:w-2/5",
    leftsubwrapper: "flex my-4",
    image: "w-1/4 p-4",
  };
  const design = {
    heading: "font-medium text-xl text-gray-700",
    separator: "bg-gray-200",
    image: "bg-gray-100 object-contain",
  };

  useEffect(() => {
    setCart(JSON.parse(sessionStorage.getItem("cart")));
  }, []);

  function removeItem(productId) {
    setCart(cart.filter((item) => item.product._id !== productId));
  }

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.leftwrapper}>
          <h2 className={design.heading}>Bag</h2>
          {cart.map((item) => (
            <div key={item.product._id}>
              <div className={position.leftsubwrapper}>
                <img
                  className={`${position.image} ${design.image}`}
                  src={item.product.image}
                  alt={item.product.name}
                />
                <Card
                  productId={item.product._id}
                  productName={item.product.name}
                  productImage={item.product.image}
                  productCategory={item.product.category}
                  productPrice={item.product.price}
                  productDiscount={item.product.discount}
                  productSize={item.size}
                  productColor={item.color}
                  onClick={removeItem}
                />
              </div>
              <hr className={design.separator} />
            </div>
          ))}
        </div>
        <div className={position.rightwrapper}>
          <h2 className={design.heading}>Summary</h2>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;

function Card(props) {
  const position = {
    container: "grid grid-cols-2 grid-rows-2 gap-4 mx-4 w-full",
    wrapper: "flex flex-col",
    rightwrapper: "items-end",
    bottomwrapper: "justify-end",
  };
  const design = {
    textstrike: "text-gray-300 line-through",
    text: "font-light text-gray-500",
    subtext: "font-light text-sm text-gray-500",
  };

  return (
    <div className={position.container}>
      <div className={position.wrapper}>
        <p>{props.productName}</p>
        <p className={design.text}>{props.productCategory} Shoes</p>
      </div>
      <div className={`${position.wrapper} ${position.rightwrapper}`}>
        <p>${getDiscountedPrice(props.productPrice, props.productDiscount)}</p>
        <p className={design.textstrike}>${props.productPrice}</p>
      </div>
      <div className={`${position.wrapper} ${position.bottomwrapper}`}>
        <p className={design.subtext}>Size: {props.productSize}</p>
        <p className={design.subtext}>Color: {props.productColor}</p>
      </div>
      <div
        className={`${position.wrapper} ${position.rightwrapper} ${position.bottomwrapper}`}
      >
        <Bin onClick={() => props.onClick(props.productId)} />
      </div>
    </div>
  );
}
