import { useState } from "react";
import Input from "../components/input";
import Button from "../components/button";
import { validate } from "../helper";

function Register() {
  const [email, setEmail] = useState("");
  const [err, setErr] = useState("");
  const position = {
    container: "w-4/6 md:w-3/6 lg:w-3/6 m-auto py-10",
    heading: "mb-7",
    button: "mt-3 *:w-full *:p-4",
  };
  const design = {
    heading: "text-2xl text-gray-700",
  };

  function onClick() {
    const { error, errMessage } = validate("email", email);
    setErr(error ? errMessage : "");
  }

  return (
    <div className={position.container}>
      <h1 className={`${position.heading} ${design.heading}`}>
        Enter your email to join or sign in.
      </h1>
      <Input
        name="email"
        value={email}
        placeholder="Email"
        onChange={(e) => setEmail(e.target.value)}
        errName={err}
      />
      <div className={position.button}>
        <Button text="Continue" onClick={onClick} />
      </div>
    </div>
  );
}

export default Register;
