import axios from "axios";

function getProduct(identifier, setProduct) {
  axios
    .post("/product", identifier)
    .then((res) => setProduct(res.data[0]))
    .catch((err) => console.log(err));
}

function getProducts(identifier, setProducts) {
  axios
    .post("/products", identifier)
    .then((res) => setProducts(res.data))
    .catch((err) => console.log(err));
}

function createCart(identifier, callback) {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  axios
    .post("/cart/create", identifier)
    .then((res) =>
      sessionStorage.setItem("cart", JSON.stringify([...cart, res.data])),
    )
    .catch((err) => console.log(err))
    .finally(() => callback());
}

function deleteCart(identifier) {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const filteredCart = cart.filter((item) => item._id !== identifier._id);
  axios
    .post("/cart/delete", identifier)
    .then(() => sessionStorage.setItem("cart", JSON.stringify(filteredCart)))
    .catch((err) => console.log(err));
}

function createUser(identifier) {
  axios.post("/user/create", identifier).catch((err) => console.log(err));
}

function createShipping(identifier) {
  axios.post("/shipping/create", identifier).catch((err) => console.log(err));
}

function postPayment(identifier, setPayment) {
  axios
    .post("/payment/create", identifier)
    .then((res) => setPayment(res.data[0]))
    .catch((err) => console.log(err));
}

export {
  getProduct,
  getProducts,
  createCart,
  deleteCart,
  createUser,
  createShipping,
};
