sourcesContent:
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Backbutton from "../components/backbutton";
import Button from "../components/button";
import Checkbox from "../components/checkbox";
import Grid from "../components/grid";
import Input from "../components/input";
import Layout from "../components/layout/layout";
import Modal from "../components/modal";
import Multiselect from "../components/multiselect";
import Picture from "../components/picture";
import Progressbar from "../components/progressbar";
import Radio from "../components/radio";
import Select from "../components/select";
import SelectCountry from "../components/selectCountry";
import SelectState from "../components/selectState";
import Textarea from "../components/textarea";
import Topbar from "../components/topbar";
import ChatIcon from "../icons/chat";
import FilterIcon from "../icons/filter";
import HouseIcon from "../icons/house";
import LinkIcon from "../icons/link";
import PrivacyIcon from "../icons/privacy";
import RemoveIcon from "../icons/remove";
import ShareIcon from "../icons/share";
import UserIcon from "../icons/user";
import {
  daysDifference,
  getTax,
  getDiscount,
  membershipDate,
  today,
  validate,
} from "../helper";
import {
  getUser,
  getOrder,
  getAddress,
  getProducts,
  getPreferences,
  deleteAddress,
  createAddress,
  createPreferences,
  updateUser,
  updatePreferences,
  updateProduct,
  updateShipping,
} from "../server";

function Dashboard() {
  const position = { container: "sm:mx-10", separator: "h-0.5 my-4" };
  const design = { separator: "bg-gray-300" };
  const [heading, setHeading] = useState("Profile");
  const { route } = useParams();

  return (
    <Layout>
      <div className={position.container}>
        <Topbar
          heading={heading}
          setHeading={setHeading}
          items={
            route === "user"
              ? ["Profile", "Inbox", "Orders", "Settings"]
              : ["Profile", "Inbox", "Orders", "Products", "Settings"]
          }
        />
        <hr className={`${position.separator} ${design.separator}`} />
        <Content heading={heading} />
      </div>
    </Layout>
  );
}

function Content(props) {
  let content;
  const position = { container: "mt-10" };
  const [user, setUser] = useState(
    JSON.parse(sessionStorage.getItem("user")) || null,
  );
  const [orders, setOrders] = useState({});
  const [allOrders, setAllOrders] = useState({});
  const [addresses, setAddresses] = useState({});
  const [preferences, setPreferences] = useState({});
  const [products, setProducts] = useState([]);

  useEffect(() => {
    if (user) {
      getOrder({ user: user._id }, setOrders);
      getOrder({}, setAllOrders);
      getAddress({ user: user._id }, setAddresses);
      getProducts({}, setProducts);
      getPreferences({ user: user._id }, setPreferences);
      getUser({ _id: user._id }, setUser);
    }
  }, []);

  switch (props.heading) {
    case "Profile":
      content = <Profile user={user} />;
      break;
    case "Inbox":
      content = <Inbox />;
      break;
    case "Orders":
      content = (
        <Orders orders={orders} allOrders={allOrders} role={user.role} />
      );
      break;
    case "Products":
      content = <Products products={products} />;
      break;
    case "Settings":
      content = (
        <Settings user={user} addresses={addresses} preferences={preferences} />
      );
      break;
    default:
      break;
  }
  return (
    <div className={position.container}>
      {user ? content : <Text>You're not logged in yet</Text>}
    </div>
  );
}

function Profile(props) {
  const position = {
    container: "flex flex-col gap-3 items-center justify-center",
  };

  return (
    <div className={position.container}>
      <Picture />
      <Heading>{props.user?.fname + " " + props.user?.lname}</Heading>
      <Text>Member since {membershipDate(props.user?.created)}</Text>
    </div>
  );
}

function Inbox() {
  return <div>Inbox</div>;
}

function Orders(props) {
  const [product, setProduct] = useState({});
  const [boolean, setBoolean] = useState(false);

  if (props.orders || props.allOrders?.cart) {
    if (!boolean) {
      if (props.role === "admin" && props.allOrders) {
        return (
          <Grid
            orders={props.allOrders}
            onClick={(item) => {
              setProduct(item);
              setBoolean(!boolean);
            }}
          />
        );
      } else if (props.role === "user" && props.orders?.cart) {
        return (
          <Grid
            orders={props.orders}
            onClick={(item) => {
              setProduct(item);
              setBoolean(!boolean);
            }}
          />
        );
      }
    } else {
      return (
        <Order
          orders={props.role === "admin" ? props.allOrders : props.orders}
          product={product}
          setProduct={setProduct}
          setBoolean={setBoolean}
          boolean={boolean}
          role={props.role}
        />
      );
    }
  } else return <Text>You don't have any orders yet.</Text>;
}

function Order(props) {
  const position = {
    container: "grid grid-cols-1 sm:grid-cols-2 gap-10 w-11/12 m-auto py-10",
    wrapper: "py-5 *:my-1",
    image: "sticky top-0 p-4",
    progressbar: "py-10",
  };
  const design = {
    image: "object-contain bg-gray-100 rounded-xl",
    progressbar: "cursor-pointer",
  };
  const [shipping, setShipping] = useState(props.orders.shipping || {});

  function startReturn() {
    updateShipping(
      {
        ...shipping,
        status: "Processing",
        returnDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000),
      },
      setShipping,
    );
  }

  function updateProgress(progress) {
    if (props.role === "admin") {
      updateShipping(
        {
          ...shipping,
          status: progress,
        },
        setShipping,
      );
    }
  }

  return (
    <>
      <Backbutton boolean={props.boolean} setBoolean={props.setBoolean} />
      <div className={position.container}>
        <img
          className={`${position.image} ${design.image}`}
          src={props.product.product.image}
          alt={props.product.product.name}
        />
        <div>
          <Subheading>{props.product.product.name}</Subheading>
          <div className={position.wrapper}>
            <Subtext>Purchased Online - {today(shipping.date)}</Subtext>
            <Subtext>
              {props.orders._id} - US${props.orders.payment.amount}
            </Subtext>
            <Subtext>
              {props.orders.user.address.city},{props.orders.user.address.state}
              ,{props.orders.user.address.country},
              {props.orders.user.address.pincode}
            </Subtext>
          </div>
          {props.role === "user" &&
            daysDifference(shipping.date, today()) < 25 &&
            shipping.status === "Delivered" && (
              <Button text="Start Return" onClick={startReturn} />
            )}
          {shipping.returnDate ? (
            <Progressbar
              className={`${position.progressbar} ${
                props.role === "admin" ? design.progressbar : ""
              }`}
              index={shipping.status}
              items={[
                "Return Placed",
                "Processing",
                "Preparing to Pickup",
                "Shipped",
                "Refunded",
              ]}
              onClick={updateProgress}
            />
          ) : (
            <Progressbar
              className={`${position.progressbar} ${
                props.role === "admin" ? design.progressbar : ""
              }`}
              index={shipping.status}
              items={[
                "Order Placed",
                "Processing",
                "Preparing to Ship",
                "Shipped",
                "Delivered",
              ]}
              onClick={updateProgress}
            />
          )}
          <Price itemOne="Subtotal" itemTwo={props.product.product.price} />
          <Price
            itemOne="Discount"
            itemTwo={getDiscount(
              props.product.product.price,
              props.product.product.discount,
            )}
            negative="true"
          />
          <Price
            itemOne="Delivery"
            itemTwo={
              daysDifference(shipping.date, shipping.created) === -7
                ? "0"
                : "50"
            }
          />
          <Price
            itemOne="Tax"
            itemTwo={getTax(
              props.product.product.price,
              props.product.product.discount,
            )}
          />
          <Price
            itemOne="Total"
            itemTwo={
              props.product.price +
              (daysDifference(shipping.date, shipping.created) === -7 ? 0 : 50)
            }
            bold="true"
          />
        </div>
      </div>
    </>
  );
}

function Products(props) {
  const [product, setProduct] = useState({});
  const [boolean, setBoolean] = useState(false);
  const [err, setErr] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    discount: "",
    slug: "",
    colors: "",
    stock: "",
  });
  const position = { container: "w-3/4 mx-auto mb-10", wrapper: "mt-8 mb-5" };

  function onChange(e) {
    const { name, value, checked } = e.target;
    if (name === "sizes" || name === "colors") {
      setProduct((prev) => ({
        ...prev,
        [name]: checked
          ? [...prev[name], value]
          : prev[name].filter((item) => item !== value),
      }));
    } else {
      setProduct((prev) => ({ ...prev, [name]: value }));
    }
  }

  function onClick() {
    let errr = {};
    Object.entries(product).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.name &&
      !errr.description &&
      !errr.category &&
      !errr.price &&
      !errr.discount &&
      !errr.slug &&
      !errr.stock
    ) {
      updateProduct(product, setProduct);
    }
  }

  if (!boolean) {
    return (
      <Grid
        products={props.products}
        onClick={(item) => {
          setProduct(item);
          setBoolean(!boolean);
        }}
      />
    );
  } else {
    return (
      <div>
        <Backbutton boolean={boolean} setBoolean={setBoolean} />
        <div className={position.container}>
          <Subheading>Update Product</Subheading>
          <div className={position.wrapper}>
            <Input
              name="name"
              placeholder="name"
              value={product.name}
              onChange={onChange}
              errName={err.name}
            />
            <Textarea
              name="description"
              placeholder="description"
              value={product.description}
              onChange={onChange}
              errName={err.description}
            />
            <Input
              name="category"
              placeholder="category"
              value={product.category}
              onChange={onChange}
              errName={err.category}
            />
            <Wrapper>
              <Input
                name="price"
                placeholder="price"
                value={product.price}
                onChange={onChange}
                errName={err.price}
              />
              <Input
                name="discount"
                placeholder="discount"
                value={product.discount}
                onChange={onChange}
                errName={err.discount}
              />
            </Wrapper>
            <Wrapper>
              <Multiselect
                name="sizes"
                placeholder="Size"
                checked={product.sizes || []}
                onChange={onChange}
                options={[
                  "5",
                  "5.5",
                  "6",
                  "6.5",
                  "7",
                  "7.5",
                  "8",
                  "8.5",
                  "9",
                  "9.5",
                  "10",
                  "10.5",
                  "11",
                  "11.5",
                  "12",
                ]}
              />
              <Multiselect
                name="colors"
                placeholder="Color"
                checked={product.colors || []}
                onChange={onChange}
                options={["green", "blue", "red", "yellow", "gray"]}
              />
            </Wrapper>
            <Input
              name="slug"
              placeholder="slug"
              value={product.slug}
              onChange={onChange}
              errName={err.slug}
            />
            <Input
              name="stock"
              placeholder="stock"
              value={product.stock}
              onChange={onChange}
              errName={err.stock}
            />
          </div>
          <Button text="Update" onClick={onClick} />
        </div>
      </div>
    );
  }
}

