import { useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";
import Button from "../components/button";
import { validate } from "../helper";

function Register() {
  const [email, setEmail] = useState();
  const [err, setErr] = useState();
  const position = {
    container: "w-3/5 md:5/12 lg:5/12 m-auto py-10",
    heading: "mb-7",
    button: "mt-3 *:w-full *:p-4",
  };
  const design = {
    heading: "text-2xl text-gray-700",
  };

  function onChange(e) {
    const { name, value } = e.target;
    setEmail((prev) => ({ ...prev, [name]: value }));
  }

  function onClick(e) {
    Object.entries(email).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    console.log(e.target, "test");
  }

  return (
    <Layout>
      <div className={position.container}>
        <h1 className={`${position.heading} ${design.heading}`}>
          Enter your email to join or sign in.
        </h1>
        <Input
          name="email"
          value={email}
          placeholder="Email"
          onChange={onChange}
          errName={err}
        />
        <div className={position.button}>
          <Button text="Continue" onClick={onClick} />
        </div>
      </div>
    </Layout>
  );
}

export default Register;
