import { useEffect, useState } from "react";

function Toast(props) {
  const [message, setMessage] = useState("");

  useEffect(
    (_) => {
      setTimeout(() => setMessage(props.message), 1000);
      setTimeout(() => setMessage(""), 6000);
    },
    [props.message],
  );

  return <div>{message}</div>;
}

export default Toast;
