import { useState, useEffect } from "react";
import CardHorizontal from "../components/cardHorizontal";
import Layout from "../components/layout/layout";
import Picture from "../components/picture";
import Input from "../components/input";
import Button from "../components/button";
import SelectCountry from "../components/selectCountry";
import SelectState from "../components/selectState";
import UserIcon from "../icons/user";
import HouseIcon from "../icons/house";
import FilterIcon from "../icons/filter";
import ChatIcon from "../icons/chat";
import ShareIcon from "../icons/share";
import LinkIcon from "../icons/link";
import { membershipDate, shippingDate, validate } from "../helper";
import { getOrder, updateUser } from "../server";

function Udashboard() {
  const [heading, setHeading] = useState("Profile");
  const position = { container: "sm:mx-10", separator: "h-0.5 my-4" };
  const design = { separator: "bg-gray-300" };

  return (
    <Layout>
      <div className={position.container}>
        <Topbar heading={heading} setHeading={setHeading} />
        <hr className={`${position.separator} ${design.separator}`} />
        <Content heading={heading} />
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex flex-col gap-10 sm:items-center sm:gap-40 sm:flex-row",
    item: "flex gap-5",
  };
  const design = {
    heading: "text-2xl",
    text: "font-medium cursor-pointer",
  };

  return (
    <div className={position.container}>
      <h3 className={design.heading}>{props.heading}</h3>
      <div className={position.item}>
        {["Profile", "Inbox", "Orders", "Favourites", "Settings"].map(
          (item) => (
            <p
              key={item}
              className={`${design.text} ${
                props.heading === item && "text-gray-500"
              }`}
              onClick={() => props.setHeading(item)}
            >
              {item}
            </p>
          ),
        )}
      </div>
    </div>
  );
}

function Content(props) {
  let content;
  const position = { container: "mt-10" };
  const user = JSON.parse(sessionStorage.getItem("user")) || null;
  const [order, setOrder] = useState({});
  const [addresses, setAddresses] = useState({});

  useEffect(() => {
    if (user) {
      getOrder({ user: user._id }, setOrder);
      getAddresses({ user: user._id }, setAddresses);
    }
  }, []);

  switch (props.heading) {
    case "Profile":
      content = <Profile user={user} />;
      break;
    case "Inbox":
      content = <Inbox />;
      break;
    case "Orders":
      content = <Orders order={order} />;
      break;
    case "Favourites":
      content = <Favourites />;
      break;
    case "Settings":
      content = <Settings user={user} addresses={addresses} />;
      break;
    default:
      break;
  }
  return (
    <div className={position.container}>
      {user ? content : <Text>You're not logged in yet</Text>}
    </div>
  );
}

function Profile(props) {
