import { useState } from "react";
import Layout from "../layout";
import Input from "../components/input";
import Button from "../components/button";

function Checkout() {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <Layout>
      <div className="w-2/4 m-auto pt-10">
        {["Delivery", "Shipping", "Billing", "Payment"].map((item, index) => (
          <div key={index} onClick={() => setActiveIndex(index)}>
            <hr className="h-px my-4 bg-gray-200" />
            <h3
              className={`text-xl font-medium mb-8 ${
                activeIndex === index ? "text-gray-900" : "text-gray-500"
              }`}
            >
              {item}
            </h3>
            {item === "Delivery" ? (
              <Delivery />
            ) : item === "Shipping" ? (
              <Shipping />
            ) : item === "Billing" ? (
              <Billing />
            ) : (
              <Payment />
            )}
          </div>
        ))}
      </div>
    </Layout>
  );
}

function Delivery() {
  const [boolean, setBoolean] = useState(false);
  const [errors, setErrors] = useState({});
  const [delivery, setDelivery] = useState({
    first_name: "",
    last_name: "",
    address: "",
    email: "",
    phone: "",
  });

  function handleChange(e) {
    const { name, value } = e.target;
    setDelivery((prevState) => ({ ...prevState, [name]: value }));
  }

  function handleSubmit() {
    let newErrors = {};
    Object.keys(delivery).forEach((key) => {
      const value = delivery[key].trim();
      switch (key) {
        case "email":
          if (!value || !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
            newErrors[key] = "Please enter a valid email";
          }
          break;
        case "phone":
          if (!value || !/^\d{10}$/.test(value)) {
            newErrors[key] = "Please enter a valid phone number";
          }
          break;
        default:
          if (!value) {
            newErrors[key] = `Please enter your ${key.replace("_", " ")}`;
          }
          break;
      }
    });
    setErrors(newErrors);
    Object.keys(newErrors).length === 0 && setBoolean(true);
  }
  return (
    <>
      {boolean === false && (
        <div>
          <h4 className="font-medium">Enter your name and address:</h4>
          <Input
            name="first_name"
            value={delivery.first_name}
            onChange={handleChange}
            placeholder="First Name"
          />
          {errors.first_name && (
            <span className="block text-sm text-red-500">
              {errors.first_name}
            </span>
          )}{" "}
          <Input
            name="last_name"
            value={delivery.last_name}
            onChange={handleChange}
            placeholder="Last Name"
          />
          {errors.last_name && (
            <span className="block text-sm text-red-500">
              {errors.last_name}
            </span>
          )}
          <Input
            name="address"
            value={delivery.address}
            onChange={handleChange}
            placeholder="Address"
          />
          {errors.address && (
            <span className="block text-sm text-red-500">{errors.address}</span>
          )}
          <h4 className="font-medium mt-10">
            What's your contact information?
          </h4>
          <Input
            name="email"
            value={delivery.email}
            onChange={handleChange}
            placeholder="Email"
          />
          {errors.email && (
            <span className="block text-sm text-red-500">{errors.email}</span>
          )}
          <Input
            name="phone"
            value={delivery.phone}
            onChange={handleChange}
            placeholder="Phone Number"
          />
          {errors.phone && (
            <span className="block text-sm text-red-500 mb-5">
              {errors.phone}
            </span>
          )}
          <Button onClick={handleSubmit} text="Continue" />
        </div>
      )}
    </>
  );
}

function Shipping() {
  const [boolean, setBoolean] = useState(false);

  return (
    <>
      {boolean && (
        <div>
          <h4>Shipping content goes here</h4>
        </div>
      )}
    </>
  );
}

function Billing() {
  return (
    <div>
      <h4>Billing content goes here</h4>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h4>Payment content goes here</h4>
    </div>
  );
}

export default Checkout;
