import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Home() {
  const [product, setProduct] = useState();
  const position = {
    heading: "text-center mt-8 mb-3",
    imagewrapper: "h-3/6",
    image: "scale-150",
    text: "m-auto w-2/4 text-center",
  };
  const design = {
    heading: "futura text-5xl uppercase tracking-tighter",
    imagewrapper: "overflow-hidden",
    image: "object-cover",
    text: "text-lg",
  };

  useEffect(() => {
    getProduct({ slug: "phoenix-flyers" }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={`${position.imagewrapper} ${design.imagewrapper}`}>
        <img
          className={`${position.image} ${design.image}`}
          src={product.image}
          alt={product.slug}
        />
      </div>
      <h1 className={`${position.heading} ${design.heading}`}>
        {product.name}
      </h1>
      <p className={`${position.text} ${design.text}`}>{product.description}</p>
    </Layout>
  );
}

export default Home;
