import { useEffect, useState } from "react";
import { Checkmark } from "react-checkmark";
import Layout from "../components/layout/layout";
import { createOrder } from "../server";

function Success() {
  const position = {
    container: "my-20",
    wrapper: "grid grid-cols-2 gap-4 w-3/4 m-auto",
    separator: "h-px w-1/4 m-auto",
  };
  const design = {
    heading: "font-medium text-2xl text-center",
    subheading: "font-medium text-xl text-center",
    subsubheading: "font-medium text-lg text-center",
    text: "text-center",
    separator: "bg-gray-200",
  };
  const [order, setOrder] = useState(
    JSON.parse(sessionStorage.getItem("order")) || {},
  );

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    const user = JSON.parse(sessionStorage.getItem("user"));
    const shipping = JSON.parse(sessionStorage.getItem("shipping"));
    const payment = JSON.parse(sessionStorage.getItem("payment"));

    !order._id &&
      createOrder(
        {
          cart: cart.map((item) => item._id),
          user: user._id,
          shipping: shipping._id,
          payment: payment._id,
        },
        setOrder,
      );
  }, [order]);

  return (
    <Layout>
      <div className={position.container}>
        <Checkmark size="10rem" />
        <h1 className={design.heading}>Order placed successfully!</h1>
        <h2 className={design.subheading}>Order ID # {order._id}</h2>
        <hr className={`${position.separator} ${design.separator}`} />
        <h2 className={design.subheading}>Total: ${order.payment.amount}</h2>
        <hr className={`${position.separator} ${design.separator}`} />
        <div className={position.wrapper}>
          <div>
            <h4 className={design.subsubheading}>Your Information</h4>
            <p className={design.text}>
              {order.user.fname} {order.user.lname}
            </p>
            <p className={design.text}>{order.user.email}</p>
            <p className={design.text}>
              {order.user.phone.replace(/(\d{3})(\d{3})(\d{4})/, "$1-$2-$3")}
            </p>
          </div>
          <>
            <h4 className={design.subsubheading}>Payment Information</h4>
          </>
          <>
            <h4 className={design.subsubheading}>Delivery Information</h4>
          </>
          <>
            <h4 className={design.subsubheading}>Shipping Information</h4>
          </>
        </div>
      </div>
    </Layout>
  );
}

export default Success;
