import { useState, useEffect } from "react";
import { getProducts } from "../server";
import Card from "./card";
import LArrowIcon from "../icons/larrow";
import RArrowIcon from "../icons/rarrow";

function Slider(props) {
  const [products, setProducts] = useState([]);
  const [start, setStart] = useState(true);
  const position = {};
  const design = {};

  useEffect(() => {
    getProducts({}, setProducts);
  }, []);

  return (
    <>
      <Topbar
        heading={props.heading}
        subheading={props.subheading}
        start={start}
      />
      {products && products.map((product) => <Card product={product} />)}
    </>
  );
}

function Topbar(props) {
  const position = {
    buttonwrapper: "flex items-center gap-3",
    container: "flex justify-between mt-10",
    larrow: "p-2.5",
    rarrow: "p-2.5",
  };
  const design = {
    heading: "text-2xl",
    larrow: "bg-gray-100 text-gray-800 rounded-3xl",
    arrowactive: props.start && "bg-gray-200 hover:bg-gray-300",
    rarrow: "bg-gray-100 text-gray-800 rounded-3xl",
  };

  return (
    <div className={position.container}>
      <h3 className={design.heading}>{props.heading}</h3>
      <div className={position.buttonwrapper}>
        <h3>{props.subheading}</h3>
        <div
          className={`${position.larrow} ${design.larrow} ${design.arrowactive}`}
        >
          <LArrowIcon />
        </div>
        <div
          className={`${position.rarrow} ${design.rarrow} ${design.arrowactive}`}
        >
          <RArrowIcon />
        </div>
      </div>
    </div>
  );
}

export default Slider;
