import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const positionStyles = {
    container: "flex md:gap-10 w-11/12 m-auto",
    imageWrapper: "sticky top-0 p-4 w-3/5",
    productWrapper: "w-2/5 p-4",
    subImage: "w-20 h-20 p-2",
    subImageWrapper: "flex gap-2 my-10",
    subSubHeading: "pt-5 *:px-2",
  };
  const designStyles = {
    image: "object-contain bg-gray-100 rounded-xl",
    subHeading: "text-gray-500",
    subImage: "border object-contain rounded-xl",
    subSubHeading: "font-medium text-lg *:text-gray-400 *:line-through",
    heading: "text-lg md:text-2xl",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={positionStyles.container}>
        <div className={positionStyles.imageWrapper}>
          <img
            className={`${positionStyles.imageWrapper} ${designStyles.image}`}
            src={product.image}
            alt={product.slug}
            style={{ height: "80vh" }}
          />
        </div>
        <div className={positionStyles.productWrapper}>
          <h1 className={designStyles.heading}>{product.name}</h1>
          <h2 className={designStyles.subHeading}>{product.category} Shoes</h2>
          <h3
            className={`${positionStyles.subSubHeading} ${designStyles.subSubHeading}`}
          >
            ${product.price - (product.price * product.discount) / 100}
            <span>${product.price}</span>
          </h3>
          <div className={positionStyles.subImageWrapper}>
            {product.colors.map((color) => (
              <img
                className={`${positionStyles.subImage} ${designStyles.subImage} bg-${color}-400`}
                src={product.image}
                alt={color}
              />
            ))}
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Product;
