import { useState } from "react";
import Input from "../components/input";
import Button from "../components/button";
import Logo from "../icons/logo";
import { validate } from "../helper";

function Register() {
  const [data, setData] = useState({ email: "", password: "" });
  const [err, setErr] = useState({ email: "", password: "" });
  const [show, setShow] = useState("email");

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (!errr.email) {
      setShow("password");
    }
    if (!errr.email && !errr.password) {
      console.log("next");
    }
  }

  return (
    <>
      {show === "email" && (
        <Form
          heading="Enter your email to join or sign in."
          name="email"
          data={data.email}
          err={err.email}
          onChange={onChange}
          onClick={onClick}
        />
      )}
      {show === "password" && (
        <Form
          heading="What's your password?"
          name="password"
          type="password"
          data={data.password}
          err={err.password}
          onChange={onChange}
          onClick={onClick}
        />
      )}
    </>
  );
}

export default Register;

function Form(props) {
  const position = {
    container: "flex flex-col gap-5 w-4/6 md:w-3/6 lg:w-3/6 m-auto py-20",
    heading: "mb-3",
    button: "*:w-full *:p-3",
  };
  const design = {
    heading: "text-3xl text-gray-700",
  };

  return (
    <div className={position.container}>
      <Logo />
      <h1 className={`${position.heading} ${design.heading}`}>
        {props.heading}
      </h1>
      <div>
        <Input
          name={props.name}
          value={props.data}
          placeholder={props.name.charAt(0).toUpperCase()}
          onChange={props.onChange}
          errName={props.err}
          type={props.type}
        />
      </div>
      <div className={position.button}>
        <Button text="Continue" onClick={props.onClick} />
      </div>
    </div>
  );
}
