import FilterIcon from "../../icons/filter";
import Menu from "../menu";

function Topbar(props) {
  const position = {
    container: "flex justify-between py-5",
    wrapper: "flex gap-5 *:flex *:gap-2",
  };
  const design = {
    heading: "font-medium text-2xl",
    text: "font-light tracking-wide cursor-pointer",
  };

  function sort(e) {
    switch (e.target.textContent) {
      case "Newest":
        setProducts(
          [...products].sort(
            (a, b) => new Date(b.created) - new Date(a.created),
          ),
        );
        break;
      case "Price: High to Low":
        setProducts([...products].sort((a, b) => b.price - a.price));
        break;
      case "Price: Low to High":
        setProducts([...products].sort((a, b) => a.price - b.price));
        break;
      default:
        break;
    }
  }

  return (
    <div className={position.container}>
      <h1 className={design.heading}>
        {props.category} Shoes ({props.products.length})
      </h1>
      <div className={position.wrapper}>
        <div>
          <h2 className={design.text}>Filters</h2>
          <FilterIcon />
        </div>
        <Menu
          text="Sort"
          subtext={["Newest", "Price: High to Low", "Price: Low to High"]}
          onClick={sort}
        />
      </div>
    </div>
  );
}

export default Topbar;
