import { useState } from "react";
import Layout from "../components/layout/layout";
import Picture from "../components/picture";
import { membershipDate } from "../helper";

function Udashboard() {
  const [heading, setHeading] = useState("Profile");
  const position = { container: "sm:mx-10", separator: "h-0.5 my-4" };
  const design = { separator: "bg-gray-300" };

  return (
    <Layout>
      <div className={position.container}>
        <Topbar heading={heading} setHeading={setHeading} />
        <hr className={`${position.separator} ${design.separator}`} />
        <Content heading={heading} />
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex flex-col gap-10 sm:items-center sm:gap-40 sm:flex-row",
    item: "flex gap-5",
  };
  const design = {
    heading: "text-2xl",
    text: "font-medium cursor-pointer",
  };

  return (
    <div className={position.container}>
      <h3 className={design.heading}>{props.heading}</h3>
      <div className={position.item}>
        {["Profile", "Inbox", "Orders", "Favourites", "Settings"].map(
          (item) => (
            <p
              className={`${design.text} ${
                props.heading === item && "text-gray-500"
              }`}
              onClick={() => props.setHeading(item)}
            >
              {item}
            </p>
          ),
        )}
      </div>
    </div>
  );
}

function Content(props) {
  let content;
  const position = { container: "mt-10" };
  const user = JSON.parse(sessionStorage.getItem("user"));

  switch (props.heading) {
    case "Profile":
      content = <Profile user={user} />;
      break;
    case "Inbox":
      content = <Inbox />;
      break;
    case "Orders":
      content = <Orders />;
      break;
    case "Favourites":
      content = <Favourites />;
      break;
    case "Settings":
      content = <Settings />;
      break;
    default:
      break;
  }
  return <div className={position.container}>{content}</div>;
}

function Profile(props) {
  const position = {
    container: "flex flex-col gap-3 items-center justify-center",
  };
  const design = { text: "text-gray-500" };

  return (
    <div className={position.container}>
      <Picture />
      <Heading>{props.user?.fname + " " + props.user?.lname}</Heading>
      <p className={design.text}>
        Member since {membershipDate(props.user?.created)}
      </p>
    </div>
  );
}

function Inbox() {
  return <div>Inbox</div>;
}

function Orders() {
  return <div>Orders</div>;
}

function Favourites() {
  return <div>Favourites</div>;
}

function Settings() {
  return <div>Settings</div>;
}

function Heading(props) {
  const design = "text-4xl";
  return <h2 className={design}>{props.children}</h2>;
}

export default Udashboard;
