import { useState, useEffect } from "react";
import axios from "axios";
import Layout from "../layout";
import Filter from "../components/filter";

function Collection(props) {
  const [products, setProducts] = useState([]);

  useEffect((_) => {
    axios
      .post("/products", { category: props.name })
      .then((res) => setProducts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <Layout>
      <div className="max-[767px]:w-11/12 min-[768px]:w-4/5 pt-20 m-auto">
        <h2 className="text-xl">
          {props.name} Shoes ({products && products.length})
        </h2>
        <div className="mt-4 *:inline-block">
          <div className="max-[767px]:w-1/4 min-[768px]:w-2/12">
            <Filter />
          </div>
          <div className="w-4/6 mx-9"></div>
        </div>
      </div>
    </Layout>
  );
}

export default Collection;
