import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Input from "../components/input";
import Button from "../components/button";

function Checkout() {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    address: "",
    email: "",
    phone: "",
    promo: "",
    cname: "",
    cnumber: "",
    date: "",
    payment: "",
  });
  const position = {
    container: "w-2/4 m-auto",
    separator: "h-px my-4",
  };
  const design = {
    separator: "bg-gray-200",
    heading: "text-xl text-gray-900 font-medium",
  };
  const items = [
    {
      name: "Delivery",
      component: <Delivery data={data} onChange={onChange} />,
    },
    {
      name: "Shipping",
      component: (
        <Shipping
          data={data}
          onChange={onChange}
          index={index}
          setIndex={setIndex}
        />
      ),
    },
    {
      name: "Payment",
      component: <Payment data={data} onChange={onChange} setData={setData} />,
    },
  ];

  useEffect(() => {
    console.log(data);
  }, [data]);

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  return (
    <Layout>
      <div className={position.container}>
        {items.map((item) => (
          <div key={item.name}>
            <h3 className={design.heading}>{item.name}</h3>
            {item.component}
            <hr className={`${position.separator} ${design.separator}`} />
          </div>
        ))}
      </div>
    </Layout>
  );
}

export default Checkout;

function Delivery(props) {
  const position = { button: "*:w-full *:p-4 my-4" };
  const items = [
    {
      name: "fname",
      value: props.data.fname,
      placeholder: "First Name",
      onChange: props.onChange,
      //errName: "Please enter your first name",
    },
    {
      name: "lname",
      value: props.data.lname,
      placeholder: "Last Name",
      onChange: props.onChange,
      //errName: "Please enter your last name",
    },
    {
      name: "address",
      value: props.data.address,
      placeholder: "Address",
      onChange: props.onChange,
      //errName: "Please enter your address",
    },
    {
      name: "email",
      value: props.data.email,
      placeholder: "Email",
      onChange: props.onChange,
      //errName: "Please enter your email",
    },
    {
      name: "phone",
      value: props.data.phone,
      placeholder: "Phone Number",
      onChange: props.onChange,
      //errName: "Please enter your phone",
    },
  ];

  return (
    <div>
      <InputGroup
        heading="What's your name and address?"
        items={items.slice(0, 3)}
      />
      <InputGroup
        heading="What's your contact information?"
        items={items.slice(3, 6)}
      />
      <div className={position.button}>
        <Button text="Continue" />
      </div>
    </div>
  );
}

function Shipping(props) {
  const position = { button: "*:w-full *:p-4 my-4" };
  const items = [
    { name: date(4) + " - " + date(7) },
    { name: date(7) + " - " + date(10) },
  ];
  function date(days) {
    const today = new Date();
    const futureDate = new Date(today);
    futureDate.setDate(today.getDate() + days);
    const options = { weekday: "short", day: "numeric", month: "short" };
    return futureDate.toLocaleDateString("en-US", options);
  }
  return (
    <div>
      <RadioGroup
        heading="When do you want the order?"
        items={items.slice(0, 2)}
        index={props.index}
        setIndex={props.setIndex}
      />
      <div className={position.button}>
        <Button text="Continue" />
      </div>
    </div>
  );
}

function Payment(props) {
  const position = { button: "*:w-full *:p-4 my-4" };
  const items = [
    {
      name: "promo",
      value: props.data.promo,
      placeholder: "Promo",
      onChange: props.onChange,
      //errName: "Please enter your promo code",
    },
    {
      name: "Credit or Debit Card",
    },
    {
      name: "Cash on Delivery",
    },
    {
      name: "cname",
      value: props.data.cname,
      placeholder: "Name on card",
      onChange: props.onChange,
      //errName: "Please enter your name on card",
    },
    {
      name: "cnumber",
      value: props.data.cnumber,
      placeholder: "Card number",
      onChange: props.onChange,
      //errName: "Please enter your card number",
    },
  ];
  return (
    <div>
      <InputGroup heading="Have a promo code?" items={items.slice(0, 1)} />
      <RadioGroup
        heading="How would you like to pay?"
        items={items.slice(1, 3)}
        index={props.index}
        setIndex={props.setIndex}
      />
      {props.index === 0 && (
        <InputGroup
          heading="Enter your payment details:"
          items={items.slice(3, 5)}
        />
      )}
      <div className={position.button}>
        <Button text="Place Order" />
      </div>
    </div>
  );
}

function InputGroup(props) {
  const position = { container: "mt-8", heading: "mb-3" };
  const design = { heading: "font-medium text-xl" };
  return (
    <div className={position.container}>
      <h4 className={`${position.heading} ${design.heading}`}>
        {props.heading}
      </h4>
      {props.items.map((item) => (
        <Input
          key={item.name}
          name={item.name}
          value={item.value}
          onChange={item.onChange}
          placeholder={item.placeholder}
          errName={item.errName}
        />
      ))}
    </div>
  );
}

function RadioGroup(props) {
  const position = {
    container: "mt-8",
    heading: "mb-3",
    item: "px-4 py-6 my-2 cursor-pointer",
  };
  const design = {
    heading: "font-medium text-xl",
    item: "border border-2 rounded-xl hover:border-gray-700",
  };

  return (
    <div className={position.container}>
      <h4 className={`${position.heading} ${design.heading}`}>
        {props.heading}
      </h4>
      {props.items.map((item, i) => (
        <p
          key={i}
          className={`${position.item} ${design.item} ${
            i === props.index && "border-gray-700"
          }`}
          onClick={() => props.setIndex(i)}
        >
          {item.name}
        </p>
      ))}
    </div>
  );
}
