import { useState } from "react";
import Eye from "../icons/eye";
import EyeClosed from "../icons/eyeclosed";

function Input(props) {
  const [boolean, setBoolean] = useState(false);
  const position = "flex justify-between w-full p-4 my-2";
  const design = {
    item: "border border-gray-300 rounded-md hover:border-gray-700 *:outline-none",
    text: "text-sm text-red-500",
  };

  return (
    <>
      <div className={`${position} ${design.item}`}>
        <input
          name={props.name}
          value={props.value}
          type={props.type}
          placeholder={props.placeholder}
          onChange={props.onChange}
        />
        <div onClick={() => setBoolean(!boolean)}>
          {props.type === "password" && !boolean ? <Eye /> : <EyeClosed />}
        </div>
      </div>
      {props.errName && <p className={design.text}>{props.errName}</p>}
    </>
  );
}

export default Input;
