import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "grid grid-cols-3 md:w-9/12 md:m-auto",
    imagewrapper: "p-4 col-span-2",
    productwrapper: "p-4",
    image: "sticky top-0",
  };
  const design = {
    heading: "text-md lg:text-2xl",
    subheading: "text-medium text-gray-500",
    image: "object-contain bg-gray-100 rounded-xl px-4 lg:p-20",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={position.container}>
        <div className={position.imagewrapper}>
          <img
            className={`${position.image} ${design.image}`}
            src={product.image}
            alt={product.slug}
            style={{ height: "80vh" }}
          />
        </div>
        <div className={position.productwrapper}>
          <h1 className={design.heading}>{product.name}</h1>
          <h2 className={design.subheading}>{product.category} Shoes</h2>
        </div>
      </div>
    </Layout>
  );
}

export default Product;
