import Layout from "../components/layout/layout";
import Button from "../components/button";

function Cart() {
  const cart = JSON.parse(sessionStorage.getItem("cart")) || [];
  const position = {
    container: "flex gap-10 w-9/12 m-auto",
  };

  function priceData() {
    const totalPrice = cart.reduce((acc, item) => acc + item.product.price, 0);
    const totalDiscountPercent = cart.reduce(
      (acc, item) => acc + item.product.discount,
      0,
    );
    const totalDiscount = (totalPrice * totalDiscountPercent) / 100;
    const totalDiscountedPrice = totalPrice - totalDiscount;
    const totalTax = totalDiscountedPrice * 0.088;
    const total = totalDiscountedPrice + totalTax;
    return { totalDiscountedPrice, totalTax, total };
  }

  function productData() {
    return cart.map((item) => ({
      size: item.size,
      color: item.color,
      image: item.product.image,
      name: item.product.name,
      category: item.product.category,
      price: item.product.price,
      discount: item.product.discount,
    }));
  }

  return (
    <Layout>
      <div className={position.container}>
        <Product productData={productData} />
        <Price priceData={priceData} />
      </div>
    </Layout>
  );
}

export default Cart;

function Product(props) {
  const position = {
    container: "w-3/5",
  };
  const design = {
    heading: "font-medium text-2xl text-gray-700",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>Bag</h1>
      {props.productData().map((item) => (
        <Card
          key={item.name}
          size={item.size}
          color={item.color}
          image={item.image}
          name={item.name}
          category={item.category}
          price={item.price}
          discount={item.discount}
        />
      ))}
    </div>
  );
}

function Price(props) {
  const { totalDiscountedPrice, totalTax, total } = props.priceData();
  const position = {
    buttonwrapper: "*:w-full *:py-3",
    container: "w-2/5",
    hr: "h-px my-5",
    wrapper: "my-5",
    itemwrapper: "flex justify-between my-3",
  };
  const design = {
    buttonwrapper: "font-medium *:bg-gray-800",
    heading: "font-medium text-2xl text-gray-700",
    itemwrapper: "text-gray-700",
    hr: "bg-gray-200",
    total: "font-medium text-gray-900",
  };

  return (
    <div className={position.container}>
      <h2 className={design.heading}>Summary</h2>
      <div className={position.wrapper}>
        <div className={`${position.itemwrapper} ${design.itemwrapper}`}>
          <span>Subtotal</span>
          <span>${totalDiscountedPrice}</span>
        </div>
        <div className={`${position.itemwrapper} ${design.itemwrapper}`}>
          <span>Tax</span>
          <span>${totalTax}</span>
        </div>
        <hr className={`${position.hr} ${design.hr}`} />
        <div className={`${position.itemwrapper} ${design.itemwrapper}`}>
          <span>Total</span>
          <span className={design.total}>${total}</span>
        </div>
        <hr className={`${position.hr} ${design.hr}`} />
      </div>
      <div className={`${position.buttonwrapper} ${design.buttonwrapper}`}>
        <Button text="Checkout" />
      </div>
    </div>
  );
}

function Card(props) {
  const position = {
    container: "flex",
  };

  return (
    <div className={position.container}>
      <Image image={props.image} name={props.name} />
      <ProductInfo
        size={props.size}
        color={props.color}
        name={props.name}
        category={props.category}
        price={props.price}
        discount={props.discount}
      />
    </div>
  );
}

function Image(props) {
  const position = {
    container: "w-1/4 h-24",
    image: "p-4",
  };
  const design = {
    image: "bg-gray-100",
  };

  return (
    <div className={position.container}>
      <img
        className={`${position.image} ${design.image}`}
        src={props.image}
        alt={props.name}
      />
    </div>
  );
}

function ProductInfo(props) {
  const position = {
    container: "flex justify-between w-3/4",
    leftwrapper: "flex flex-col",
    rightwrapper: "flex flex-col",
  };
  const discount = (props.price * props.discount) / 100;

  return (
    <div className={position.container}>
      <div className={position.leftwrapper}>
        <span>{props.name}</span>
        <span>{props.category} Shoes</span>
        <span>Size: {props.size}</span>
        <span>Color: {props.color}</span>
      </div>
      <div className={position.rightwrapper}>
        <span>${props.price - discount}</span>
        <span>${props.price}</span>
      </div>
    </div>
  );
}
