import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Card from "../components/card";
import Filters from "../components/filters";
import DarrowIcon from "../icons/darrow";
import FilterIcon from "../icons/filter";
import { getProducts } from "../server";

function Collection(props) {
  const [products, setProducts] = useState([]);
  const [showFilter, setShowFilter] = useState(true);
  const [showSort, setShowSort] = useState(true);
  const position = {
    container: "flex gap-2",
    fwrapper: showFilter ? "px-4" : "hidden",
    swrapper: showSort ? "px-4" : "hidden",
    pwrapper:
      "grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-6 gap-4 px-4",
  };

  useEffect(() => {
    getProducts({ category: props.category }, setProducts);
  }, [props.category]);

  function sort(basis) {
    switch (basis) {
      case "new":
        setProducts(
          [...props.products].sort(
            (a, b) => new Date(b.created) - new Date(a.created),
          ),
        );

        break;
      case "phigh":
        break;
      case "plow":
        break;
      default:
        break;
    }
  }

  return (
    <Layout>
      <Topbar
        category={props.category}
        products={products}
        showFilter={showFilter}
        setShowFilter={setShowFilter}
        showSort={showSort}
        setShowSort={setShowSort}
        sort={sort}
      />
      <div className={position.container}>
        <div className={position.fwrapper}>
          <Filters />
        </div>
        <div className={position.pwrapper}>
          {products &&
            products.map((product) => (
              <Card key={product._id} product={product} />
            ))}
        </div>
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex justify-between px-5 py-5",
    wrapper: "flex gap-5",
    text: "flex gap-1",
    dropdowncontainer: "relative",
    dropdown: props.showSort ? "absolute w-36 right-0 p-3" : "hidden",
  };
  const design = {
    heading: "font-medium text-2xl",
    text: "font-light tracking-wide cursor-pointer",
    dropdown: "bg-white border border-white rounded-2xl",
    dropdownitem: "hover:cursor-pointer hover:text-gray-400",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>
        {props.category} Shoes ({props.products.length})
      </h1>
      <div className={position.wrapper}>
        <div onClick={() => props.setShowFilter(!props.showFilter)}>
          <h2 className={`${position.text} ${design.text}`}>
            Show Filters <FilterIcon />
          </h2>
        </div>
        <div
          className={position.dropdowncontainer}
          onClick={() => props.setShowSort(!props.showSort)}
        >
          <h2 className={`${position.text} ${design.text}`}>
            Sort By <DarrowIcon />
          </h2>
          <div className={`${position.dropdown} ${design.dropdown}`}>
            <p
              onClick={() => props.sort("new")}
              className={design.dropdownitem}
            >
              Newest
            </p>
            <p
              onClick={() => props.sort("phigh")}
              className={design.dropdownitem}
            >
              Price: High-Low
            </p>
            <p
              onClick={() => props.sort("plow")}
              className={design.dropdownitem}
            >
              Price: Low-High
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Collection;
