import { useState, useEffect } from "react";
function Toast(props) {
  const [message, setMessage] = useState("");

  message && setMessage(props.message);

  return (
    message && (
      <div className="fixed bg-gray-800 text-white rounded-md py-3 px-5 z-10 top-4 right-1/2">
        {message}
      </div>
    )
  );
}

export default Toast;
