import { useState, useEffect } from "react";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Home() {
  const [product, setProduct] = useState();
  const position = {
    bannerwrapper: "",
    banner: "",
  };
  const design = {
    bannerwrapper: "overflow-hidden h-3/6",
    banner: "object-cover scale-150",
  };

  useEffect(() => {
    getProduct({ slug: "phoenix-flyers" }, setProduct);
  }, []);

  return (
    <Layout>
      <div className={`${position.bannerwrapper} ${design.bannerwrapper}`}>
        <img
          className={`${position.banner} ${design.banner}`}
          src={product.image}
          alt={product.slug}
        />
      </div>
    </Layout>
  );
}

export default Home;
