import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "flex md:gap-10 w-11/12 m-auto",
    wrapper: "w-2/5 p-4",
    subsubheading: "pt-5 *:px-2",
  };
  const design = {
    subheading: "text-gray-500",
    subsubheading: "font-medium text-lg *:text-gray-400 *:line-through",
    heading: "text-lg md:text-2xl",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, [slug]);

  return (
    <Layout>
      {Object.keys(product).length && (
        <div className={position.container}>
          <Image product={product} />
          <ProductInfo product={product} />
        </div>
      )}
    </Layout>
  );
}

export default Product;

function Image(props) {
  const position = { container: "w-3/5 p-4", item: "sticky top-0 p-4" };
  const design = { item: "object-contain bg-gray-100 rounded-xl" };

  return (
    <div className={position.container}>
      <img
        className={`${position.item} ${design.item}`}
        src={props.product.image}
        alt={props.product.slug}
        style={{ height: "80vh" }}
      />
    </div>
  );
}

function ProductInfo(props) {
  return (
    <div className={position.wrapper}>
      <h1 className={design.heading}>{product.name}</h1>
      <h2 className={design.subheading}>{product.category} Shoes</h2>
      <h3 className={`${position.subsubheading} ${design.subsubheading}`}>
        ${product.price - (product.price * product.discount) / 100}
        <span>${product.price}</span>
      </h3>
      <Colors product={props.product} />
    </div>
  );
}

function Colors(props) {
  const position = { container: "flex gap-1 my-10", item: "w-20 h-20 p-2" };
  const design = { item: "border object-contain rounded-md" };

  return (
    <div className={position.container}>
      {props.product.colors.map((color) => (
        <img
          className={`bg-${color}-400 ${position.item} ${design.item}`}
          src={props.product.image}
          alt={color}
        />
      ))}
    </div>
  );
}
