import { useState, useEffect } from "react";
import Button from "../components/button";
import CardHorizontal from "../components/cardHorizontal";
import Checkbox from "../components/checkbox";
import Input from "../components/input";
import Layout from "../components/layout/layout";
import Modal from "../components/modal";
import Picture from "../components/picture";
import Progressbar from "../components/progressbar";
import Radio from "../components/radio";
import Select from "../components/select";
import SelectCountry from "../components/selectCountry";
import SelectState from "../components/selectState";
import ChatIcon from "../icons/chat";
import FilterIcon from "../icons/filter";
import HouseIcon from "../icons/house";
import LarrowIcon from "../icons/larrow";
import LinkIcon from "../icons/link";
import PrivacyIcon from "../icons/privacy";
import RemoveIcon from "../icons/remove";
import ShareIcon from "../icons/share";
import UserIcon from "../icons/user";
import {
  daysDifference,
  getTax,
  getDiscount,
  membershipDate,
  shippingDate,
  today,
  validate,
} from "../helper";
import {
  getUser,
  getOrder,
  getAddress,
  getPreferences,
  deleteAddress,
  createAddress,
  createPreferences,
  updateUser,
  updatePreferences,
  updateShipping,
} from "../server";

function Udashboard() {
  const [heading, setHeading] = useState("Profile");
  const position = { container: "sm:mx-10", separator: "h-0.5 my-4" };
  const design = { separator: "bg-gray-300" };

  return (
    <Layout>
      <div className={position.container}>
        <Topbar heading={heading} setHeading={setHeading} />
        <hr className={`${position.separator} ${design.separator}`} />
        <Content heading={heading} />
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex flex-col gap-10 sm:items-center sm:gap-40 sm:flex-row",
    item: "flex gap-5",
  };
  const design = {
    heading: "text-2xl",
    text: "font-medium cursor-pointer",
  };

  return (
    <div className={position.container}>
      <h3 className={design.heading}>{props.heading}</h3>
      <div className={position.item}>
        {["Profile", "Inbox", "Orders", "Settings"].map((item) => (
          <p
            key={item}
            className={`${design.text} ${
              props.heading === item && "text-gray-500"
            }`}
            onClick={() => props.setHeading(item)}
          >
            {item}
          </p>
        ))}
      </div>
    </div>
  );
}

function Content(props) {
  let content;
  const position = { container: "mt-10" };
  const [user, setUser] = useState(
    JSON.parse(sessionStorage.getItem("user")) || null,
  );
  const [order, setOrder] = useState({});
  const [addresses, setAddresses] = useState({});
  const [preferences, setPreferences] = useState({});

  useEffect(() => {
    if (user) {
      getOrder({ user: user._id }, setOrder);
      getAddress({ user: user._id }, setAddresses);
      getPreferences({ user: user._id }, setPreferences);
      getUser({ _id: user._id }, setUser);
    }
  }, []);

  switch (props.heading) {
    case "Profile":
      content = <Profile user={user} />;
      break;
    case "Inbox":
      content = <Inbox />;
      break;
    case "Orders":
      content = <Orders order={order} />;
      break;
    case "Settings":
      content = (
        <Settings user={user} addresses={addresses} preferences={preferences} />
      );
      break;
    default:
      break;
  }
  return (
    <div className={position.container}>
      {user ? content : <Text>You're not logged in yet</Text>}
    </div>
  );
}

function Profile(props) {
  const position = {
    container: "flex flex-col gap-3 items-center justify-center",
  };

  return (
    <div className={position.container}>
      <Picture />
      <Heading>{props.user?.fname + " " + props.user?.lname}</Heading>
      <Text>Member since {membershipDate(props.user?.created)}</Text>
    </div>
  );
}

function Inbox() {
  return <div>Inbox</div>;
}

function Orders(props) {
  const position = {
    container: "grid grid-cols-1 md:grid-cols-2",
    card: "w-2/4 h-40",
  };
  const [data, setData] = useState(null);

  if (props.order) {
    return !data ? (
      <div className={position.container}>
        {props.order.cart.map((item) => (
          <CardHorizontal
            key={item._id}
            className={position.card}
            badge={
              props.order.shipping.status === "Processing"
                ? props.order.shipping.returnDate === null
                  ? `Delivery by ${shippingDate(props.order.shipping.date)}`
                  : `Pickup by ${shippingDate(props.order.shipping.returnDate)}`
                : props.order.shipping.status
            }
            cart={item}
            product={item.product}
            onClick={() => setData(item.product)}
          />
        ))}
      </div>
    ) : (
      <Order order={props.order} data={data} setData={setData} />
    );
  } else return <Text>You don't have any orders yet.</Text>;
}

function Order(props) {
  const position = {
    container: "grid grid-cols-1 sm:grid-cols-2 gap-10 w-3/4 m-auto py-10",
    wrapper: "py-5 *:my-1",
    image: "sticky top-0 p-4",
    progressbar: "py-10",
  };
  const design = {
    button: "cursor-pointer",
    image: "object-contain bg-gray-100 rounded-xl",
  };
  const isReturned = useState(props.order.shipping.returnDate);
  console.log(isReturned);

  function startReturn() {
    updateShipping({
      ...props.order.shipping,
      returnDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000),
    });
  }
  return (
    <>
      <LarrowIcon
        className={design.button}
        onClick={() => props.setData(null)}
      />
      <div className={position.container}>
        <img
          className={`${position.image} ${design.image}`}
          src={props.data.image}
          alt={props.data.name}
        />
        <div>
          <Subheading>{props.data.name}</Subheading>
          <div className={position.wrapper}>
            <Subtext>
              Purchased Online - {today(props.order.shipping.date)}
            </Subtext>
            <Subtext>
              {props.order._id} - US${props.order.payment.amount}
            </Subtext>
            <Subtext>
              {props.order.user.address.city},{props.order.user.address.state},
              {props.order.user.address.country},
              {props.order.user.address.pincode}
            </Subtext>
          </div>
          {!isReturned &&
            daysDifference(props.order.shipping.date, today()) < 25 &&
            props.order.shipping.status === "Delivered" && (
              <Button text="Start Return" onClick={startReturn} />
            )}
          {isReturned ? (
            <Progressbar
              className={position.progressbar}
              index={props.order.shipping.status}
              items={[
                "Return Placed",
                "Processing",
                "Preparing to Pickup",
                "Shipped",
                "Refunded",
              ]}
            />
          ) : (
            <Progressbar
              className={position.progressbar}
              index={props.order.shipping.status}
              items={[
                "Order Placed",
                "Processing",
                "Preparing to Ship",
                "Shipped",
                "Delivered",
              ]}
            />
          )}
          <Price itemOne="Subtotal" itemTwo={props.data.price} />
          <Price
            itemOne="Discount"
            itemTwo={getDiscount(props.data.price, props.data.discount)}
            negative="true"
          />
          <Price
            itemOne="Delivery"
            itemTwo={
              daysDifference(
                props.order.shipping.date,
                props.order.shipping.created,
              ) === -7
                ? "0"
                : "50"
            }
          />
          <Price
            itemOne="Tax"
            itemTwo={getTax(props.data.price, props.data.discount)}
          />
          <Price
            itemOne="Total"
            itemTwo={props.order.payment.amount}
            bold="true"
          />
        </div>
      </div>
    </>
  );
}

function Price(props) {
  const position = "flex justify-between mt-3";
  return (
    <>
      {props.bold ? (
        <>
          <hr className={position} />
          <div className={position}>
            <p>{props.itemOne}</p>
            <p>${props.itemTwo}</p>
          </div>
        </>
      ) : props.negative ? (
        <>
          <div className={position}>
            <Subtext>{props.itemOne}</Subtext>
            <Subtext>- ${props.itemTwo}</Subtext>
          </div>
        </>
      ) : (
        <div className={position}>
          <Subtext>{props.itemOne}</Subtext>
          <Subtext>${props.itemTwo}</Subtext>
        </div>
      )}
    </>
  );
}

function Settings(props) {
  const position = {
    container: "grid grid-cols-1 md:grid-cols-2 gap-10",
    wrapper: "w-3/4 py-2",
  };
  const [content, setContent] = useState(<AccountDetails user={props.user} />);

  return (
    <div className={position.container}>
      <Sidebar
        setContent={setContent}
        user={props.user}
        addresses={props.addresses}
        preferences={props.preferences}
      />
      <div className={position.wrapper}>{content}</div>
    </div>
  );
}

function Sidebar(props) {
  const position = {
    container: "*:flex *:gap-5 *:mb-5",
    icon: "w-6 h-6",
  };
  const design = { container: "*:cursor-pointer" };

  function onClick(e) {
    switch (e.target.innerHTML) {
      case "Account Details":
        props.setContent(<AccountDetails user={props.user} />);
        break;
      case "Delivery Addresses":
        props.setContent(
          <DeliveryAddresses user={props.user} addresses={props.addresses} />,
        );
        break;
      case "Shop Preferences":
        props.setContent(
          <ShopPreferences user={props.user} preferences={props.preferences} />,
        );
        break;
      case "Communication Preferences":
        props.setContent(
          <CommunicationPreferences
            user={props.user}
            preferences={props.preferences}
          />,
        );
        break;
      case "Profile Visibility":
        props.setContent(
          <ProfileVisibility
            user={props.user}
            preferences={props.preferences}
          />,
        );
        break;
      case "Linked Accounts":
        props.setContent(<LinkedAccounts />);
        break;
    }
  }

  return (
    <div className={`${position.container} ${design.container}`}>
      <div>
        <UserIcon className={position.icon} />
        <p onClick={onClick}>Account Details</p>
      </div>
      <div>
        <HouseIcon />
        <p onClick={onClick}>Delivery Addresses</p>
      </div>
      <div>
        <FilterIcon />
        <p onClick={onClick}>Shop Preferences</p>
      </div>
      <div>
        <ChatIcon />
        <p onClick={onClick}>Communication Preferences</p>
      </div>
      <div>
        <ShareIcon />
        <p onClick={onClick}>Profile Visibility</p>
      </div>
      <div>
        <LinkIcon />
        <p onClick={onClick}>Linked Accounts</p>
      </div>
    </div>
  );
}

function AccountDetails(props) {
  const [data, setData] = useState({
    email: props.user.email,
    password: props.user.password,
    phone: props.user.phone,
    country: props.user.address.country || "",
    state: props.user.address.state || "",
    city: props.user.address.city || "",
    pincode: props.user.address.pincode || "",
  });
  const [err, setErr] = useState({
    email: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
  });
  const [country, setCountry] = useState(props.user.address.country);
  const position = { container: "my-5" };

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.email &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      const user = {
        _id: props.user._id,
        address: {
          street: data.street,
          building: data.building,
          city: data.city,
          state: data.state,
          country: data.country,
          pincode: data.pincode,
        },
        email: data.email,
        fname: props.user.fname,
        lname: props.user.lname,
        password: data.password,
        phone: data.phone,
      };
      updateUser(user, (res) => {
        setData(res);
        sessionStorage.setItem("user", JSON.stringify(res));
      });
    }
  }

  return (
    <>
      <Subheading>Account Details</Subheading>
      <div className={position.container}>
        <Input
          name="email"
          placeholder="Email*"
          value={data.email}
          onChange={onChange}
          errName={err.email}
        />
        <Input
          name="password"
          placeholder="Password"
          type="password"
          value={data.password}
          onChange={onChange}
        />
        <Input
          name="phone"
          placeholder="Phone"
          value={data.phone}
          onChange={onChange}
        />
        <SelectCountry
          name="country"
          value={data.country}
          onChange={onChange}
          placeholder="Country/Region*"
          setCountry={setCountry}
          errName={err.country}
        />
        <SelectState
          name="state"
          value={data.state}
          onChange={onChange}
          placeholder="State*"
          country={country}
          errName={err.state}
        />
        <Input
          name="city"
          placeholder="Town/City*"
          value={data.city}
          onChange={onChange}
          errName={err.city}
        />
        <Input
          name="pincode"
          placeholder="Postcode*"
          value={data.pincode}
          onChange={onChange}
          errName={err.pincode}
        />
      </div>
      <Button text="Save" onClick={onClick} />
    </>
  );
}

function DeliveryAddresses(props) {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    street: "",
    building: "",
    city: "",
    pincode: "",
    country: "",
    state: "",
    phone: "",
  });
  const [err, setErr] = useState({
    fname: "",
    lname: "",
    phone: "",
    street: "",
    building: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });
  const [addresses, setAddresses] = useState(props.addresses);
  const [country, setCountry] = useState(null);
  const [boolean, setBoolean] = useState(false);
  const position = {
    container: "my-5 relative",
    badge: "w-5 h-5 absolute right-40 hover:scale-100 hover:cursor-pointer",
  };

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }
  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.fname &&
      !errr.lname &&
      !errr.phone &&
      !errr.street &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      createAddress(
        {
          user: props.user._id,
          fname: data.fname,
          lname: data.lname,
          phone: data.phone,
          address: {
            street: data.street,
            building: data.building,
            city: data.city,
            state: data.state,
            country: data.country,
            pincode: data.pincode,
          },
        },
        (res) => {
          setAddresses(res);
        },
      );
      setBoolean(false);
    }
  }

  return (
    <>
      <Subheading>Delivery Addresses</Subheading>
      <div className={position.container}>
        {addresses.length > 0 &&
          addresses.map((item) => (
            <div className={position.container}>
              <RemoveIcon
                className={position.badge}
                onClick={() => deleteAddress({ _id: item._id }, setAddresses)}
              />
              <Text>
                {item.fname} {item.lname}
              </Text>
              <Text>{item.address.street}</Text>
              <Text>{item.address.building}</Text>
              <Text>
                {item.address.city}, {item.address.state} {item.address.pincode}
              </Text>
            </div>
          ))}
        {boolean && (
          <Modal boolean={boolean} setBoolean={setBoolean}>
            <Subheading>Add Address</Subheading>
            <div className={position.container}>
              <Wrapper>
                <Input
                  name="fname"
                  placeholder="First Name*"
                  value={data.fname}
                  onChange={onChange}
                  errName={err.fname}
                />
                <Input
                  name="lname"
                  placeholder="Last Name*"
                  value={data.lname}
                  onChange={onChange}
                  errName={err.lname}
                />
              </Wrapper>
              <Input
                name="street"
                placeholder="Street Address*"
                value={data.street}
                onChange={onChange}
                errName={err.street}
              />
              <Input
                name="building"
                placeholder="Apt, Suite, Building"
                value={data.building}
                onChange={onChange}
                errName={err.building}
              />
              <Wrapper>
                <Input
                  name="city"
                  placeholder="Town/City*"
                  value={data.city}
                  onChange={onChange}
                  errName={err.city}
                />
                <Input
                  name="pincode"
                  placeholder="Postcode*"
                  value={data.pincode}
                  onChange={onChange}
                  errName={err.pincode}
                />
              </Wrapper>
              <Wrapper>
                <SelectCountry
                  name="country"
                  value={data.country}
                  onChange={onChange}
                  placeholder="Country*"
                  setCountry={setCountry}
                  errName={err.country}
                />
                <SelectState
                  name="state"
                  value={data.state}
                  onChange={onChange}
                  placeholder="State*"
                  country={country}
                  errName={err.state}
                />
              </Wrapper>
              <Input
                name="phone"
                placeholder="Phone Number*"
                value={data.phone}
                onChange={onChange}
                errName={err.phone}
              />
            </div>
            <Button text="Save" onClick={onClick} />
          </Modal>
        )}
      </div>
      <Button text="Add Address" onClick={() => setBoolean(!boolean)} />
    </>
  );
}

function ShopPreferences(props) {
  const [data, setData] = useState({
    size: props.preferences?.size || "",
    gender: props.preferences?.gender || "",
    category: props.preferences?.category || [],
    measure: props.preferences?.measure || "",
    user: props.user._id,
  });
  const position = { container: "*:mb-5" };

  function onChange(e) {
    const { name, value, checked } = e.target;
    if (name === "category") {
      setData((prev) => ({
        ...prev,
        category: checked
          ? [...prev.category, value]
          : prev.category.filter((item) => item !== value),
      }));
    } else setData((prev) => ({ ...prev, [name]: value }));
  }
  function onClick() {
    props.preferences?._id ? updatePreferences(data) : createPreferences(data);
  }

  return (
    <>
      <div className={position.container}>
        <Subheading>Shop Preferences</Subheading>
        <Select
          name="size"
          placeholder="Shoe Size*"
          value={data.size}
          onChange={onChange}
          options={[
            { name: "5", value: 5 },
            { name: "5.5", value: 5.5 },
            { name: "6", value: 6 },
            { name: "6.5", value: 6.5 },
            { name: "7", value: 7 },
            { name: "7.5", value: 7.5 },
            { name: "8", value: 8 },
            { name: "8.5", value: 8.5 },
            { name: "9", value: 9 },
            { name: "9.5", value: 9.5 },
            { name: "10", value: 10 },
            { name: "10.5", value: 10.5 },
            { name: "11", value: 11 },
            { name: "11.5", value: 11.5 },
            { name: "12", value: 12 },
          ]}
        />
        <Subsubheading>Shopping Preferences</Subsubheading>
        <Text>
          <Radio
            name="gender"
            options={["Men's", "Women's"]}
            onChange={onChange}
            checked={data.gender}
          />
        </Text>
        <Subsubheading>Additional Preferences</Subsubheading>
        <Text>
          <Checkbox
            name="category"
            options={["Men's", "Women's", "Girls'", "Boys'"]}
            onChange={onChange}
            checked={data.category}
          />
        </Text>
        <Subsubheading>Unit of Measure</Subsubheading>
        <Text>
          <Radio
            name="measure"
            options={["Metric", "Imperial"]}
            onChange={onChange}
            checked={data.measure}
          />
        </Text>
      </div>
      <Button text="Save" onClick={onClick} />
    </>
  );
}

