function Orders(props) {
  const [product, setProduct] = useState({});
  const [boolean, setBoolean] = useState(false);

  if (props.orders || props.allOrders?.cart) {
    if (!boolean) {
      if (props.role === "admin" && props.allOrders) {
        return (
          <Grid
            orders={props.allOrders}
            onClick={(item) => {
              setProduct(item);
              setBoolean(!boolean);
            }}
          />
        );
      } else if (props.role === "user" && props.orders?.cart) {
        return (
          <Grid
            orders={props.orders}
            onClick={(item) => {
              setProduct(item);
              setBoolean(!boolean);
            }}
          />
        );
      }
    } else {
      return (
        <Order
          orders={props.role === "admin" ? props.allOrders : props.orders}
          product={product}
          setProduct={setProduct}
          setBoolean={setBoolean}
          boolean={boolean}
          role={props.role}
        />
      );
    }
  } else return <Text>You don't have any orders yet.</Text>;
}

function Order(props) {
  const position = {
    container: "grid grid-cols-1 sm:grid-cols-2 gap-10 w-11/12 m-auto py-10",
    wrapper: "py-5 *:my-1",
    image: "sticky top-0 p-4",
    progressbar: "py-10",
  };
  const design = {
    image: "object-contain bg-gray-100 rounded-xl",
    progressbar: "cursor-pointer",
  };
  const [shipping, setShipping] = useState(props.orders.shipping || {});

  function startReturn() {
    /*createShipping(
      {
        ...shipping,
        status: "Return Placed",
        history: { ...props.orders.shipping.history, status: "Return Placed" },
      },
      setShipping,
    );*/
    console.log(props.orders.shipping.history);
  }

  function updateProgress(progress) {
    if (props.role === "admin") {
      updateShipping(
        {
          ...shipping,
          status: progress,
          history: [
            ...shipping.history,
            { status: progress, created: new Date() },
          ],
        },
        setShipping,
      );
    }
  }

  return (
    <>
      <Backbutton boolean={props.boolean} setBoolean={props.setBoolean} />
      <div className={position.container}>
        <img
          className={`${position.image} ${design.image}`}
          src={props.product.product.image}
          alt={props.product.product.name}
        />
        <div>
          <Subheading>{props.product.product.name}</Subheading>
          <div className={position.wrapper}>
            <Subtext>Purchased Online - {fullDate(shipping.date)}</Subtext>
            <Subtext>
              {props.orders._id} - US${props.orders.payment.amount}
            </Subtext>
            <Subtext>
              {props.orders.user.address.city},{props.orders.user.address.state}
              ,{props.orders.user.address.country},
              {props.orders.user.address.pincode}
            </Subtext>
          </div>
          {props.role === "user" &&
            daysDifference(shipping.date, fullDate(new Date())) < 25 &&
            shipping.status === "Delivered" && (
              <Button text="Start Return" onClick={startReturn} />
            )}
          {shipping.returnDate ? (
            <Progressbar
              className={`${position.progressbar} ${
                props.role === "admin" ? design.progressbar : ""
              }`}
              index={shipping.status}
              items={[
                "Return Placed",
                "Processing",
                "Preparing to Pickup",
                "Shipped",
                "Refunded",
              ]}
              onClick={updateProgress}
            />
          ) : (
            <Progressbar
              className={`${position.progressbar} ${
                props.role === "admin" ? design.progressbar : ""
              }`}
              index={shipping.status}
              items={[
                "Order Placed",
                "Processing",
                "Preparing to Ship",
                "Shipped",
                "Delivered",
              ]}
              onClick={updateProgress}
            />
          )}
          <Price itemOne="Subtotal" itemTwo={props.product.product.price} />
          <Price
            itemOne="Discount"
            itemTwo={getDiscount(
              props.product.product.price,
              props.product.product.discount,
            )}
            negative="true"
          />
          <Price
            itemOne="Delivery"
            itemTwo={
              daysDifference(shipping.date, shipping.created) === -7
                ? "0"
                : "50"
            }
          />
          <Price
            itemOne="Tax"
            itemTwo={getTax(
              props.product.product.price,
              props.product.product.discount,
            )}
          />
          <Price
            itemOne="Total"
            itemTwo={
              props.product.price +
              (daysDifference(shipping.date, shipping.created) === -7 ? 0 : 50)
            }
            bold="true"
          />
        </div>
      </div>
    </>
  );
}

function Products(props) {
  const position = {
    addicon: "mb-5",
  };
  const design = {
    addicon: "text-gray-800 scale-125 cursor-pointer",
  };
  const [boolean, setBoolean] = useState(false);
  const [route, setRoute] = useState(null);
  const [product, setProduct] = useState({
    name: props.product?.name || "",
    description: props.product?.description || "",
    category: props.product?.category || "",
    price: props.product?.price || "",
    discount: props.product?.discount || "",
    slug: "",
    stock: props.product?.stock || "",
    sizes: props.product?.sizes || [],
    colors: props.product?.colors || [],
    image: props.product?.image || "",
  });
  const [err, setErr] = useState({
    name: "",
    description: "",
    category: "",
    price: "",
    discount: "",
    slug: "",
    colors: "",
    stock: "",
  });

  function onChange(e) {
    const { name, value, files, checked } = e.target;
    if (name === "sizes" || name === "colors") {
      if (name === "sizes") {
        setProduct((prev) => ({
          ...prev,
          sizes: checked
            ? [...prev.sizes, parseFloat(value)]
            : prev.sizes.filter((item) => item !== parseFloat(value)),
        }));
      } else {
        setProduct((prev) => ({
          ...prev,
          colors: checked
            ? [...prev.colors, value]
            : prev.colors.filter((item) => item !== value),
        }));
      }
    } else if (name === "image") {
      setProduct((prev) => ({ ...prev, image: files[0] }));
    } else {
      setProduct((prev) => ({ ...prev, [name]: value }));
    }
  }

  function onClick() {
    let errr = {};
    Object.entries(product).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.name &&
      !errr.description &&
      !errr.category &&
      !errr.price &&
      !errr.discount &&
      !errr.stock
    ) {
      route === "Create"
        ? createProduct(product, setProduct)
        : updateProduct(product, setProduct);
    }
  }

  if (!boolean) {
    return (
      <>
        <AddIcon
          className={`${position.addicon} ${design.addicon}`}
          onClick={() => {
            setRoute("Create");
            setBoolean(!boolean);
          }}
        />
        <Grid
          products={props.products}
          onClick={(item) => {
            setProduct(item);
            setRoute("Update");
            setBoolean(!boolean);
          }}
        />
      </>
    );
  } else {
    return (
      <Product
        route={route}
        boolean={boolean}
        setRoute={setRoute}
        setBoolean={setBoolean}
        product={product}
        title={route}
        onChange={onChange}
        onClick={onClick}
        err={err}
      />
    );
  }
}

