function AccountDetails(props) {
  const [data, setData] = useState({
    email: props.user.email,
    password: props.user.password,
    phone: props.user.phone,
    country: props.user.address.country || "",
    state: props.user.address.state || "",
    city: props.user.address.city || "",
    pincode: props.user.address.pincode || "",
  });
  const [err, setErr] = useState({
    email: "",
    country: "",
    state: "",
    city: "",
    pincode: "",
  });
  const [country, setCountry] = useState(props.user.address.country);
  const position = { container: "my-5" };

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }

  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.email &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      const user = {
        _id: props.user._id,
        address: {
          street: data.street,
          building: data.building,
          city: data.city,
          state: data.state,
          country: data.country,
          pincode: data.pincode,
        },
        email: data.email,
        fname: props.user.fname,
        lname: props.user.lname,
        password: data.password,
        phone: data.phone,
      };
      updateUser(user, (res) => {
        setData(res);
        sessionStorage.setItem("user", JSON.stringify(res));
      });
    }
  }

  return (
    <>
      <Subheading>Account Details</Subheading>
      <div className={position.container}>
        <Input
          name="email"
          placeholder="Email*"
          value={data.email}
          onChange={onChange}
          errName={err.email}
        />
        <Input
          name="password"
          placeholder="Password"
          type="password"
          value={data.password}
          onChange={onChange}
        />
        <Input
          name="phone"
          placeholder="Phone"
          value={data.phone}
          onChange={onChange}
        />
        <SelectCountry
          name="country"
          value={data.country}
          onChange={onChange}
          placeholder="Country/Region*"
          setCountry={setCountry}
          errName={err.country}
        />
        <SelectState
          name="state"
          value={data.state}
          onChange={onChange}
          placeholder="State*"
          country={country}
          errName={err.state}
        />
        <Input
          name="city"
          placeholder="Town/City*"
          value={data.city}
          onChange={onChange}
          errName={err.city}
        />
        <Input
          name="pincode"
          placeholder="Postcode*"
          value={data.pincode}
          onChange={onChange}
          errName={err.pincode}
        />
      </div>
      <Button text="Save" onClick={onClick} />
    </>
  );
}

function DeliveryAddresses(props) {
  const [data, setData] = useState({
    fname: "",
    lname: "",
    street: "",
    building: "",
    city: "",
    pincode: "",
    country: "",
    state: "",
    phone: "",
  });
  const [err, setErr] = useState({
    fname: "",
    lname: "",
    phone: "",
    street: "",
    building: "",
    city: "",
    state: "",
    country: "",
    pincode: "",
  });
  const [addresses, setAddresses] = useState(props.addresses);
  const [country, setCountry] = useState(null);
  const [boolean, setBoolean] = useState(false);
  const position = {
    container: "my-5 relative",
    badge: "w-5 h-5 absolute right-40 hover:scale-100 hover:cursor-pointer",
  };

  function onChange(e) {
    const { name, value } = e.target;
    setData((prev) => ({ ...prev, [name]: value }));
  }
  function onClick() {
    let errr = {};
    Object.entries(data).forEach(([name, value]) => {
      const { error, errMessage } = validate(name, value);
      errr = { ...errr, [name]: error ? errMessage : "" };
      setErr((prev) => ({ ...prev, [name]: error ? errMessage : "" }));
    });
    if (
      !errr.fname &&
      !errr.lname &&
      !errr.phone &&
      !errr.street &&
      !errr.city &&
      !errr.state &&
      !errr.country &&
      !errr.pincode
    ) {
      createAddress(
        {
          user: props.user._id,
          fname: data.fname,
          lname: data.lname,
          phone: data.phone,
          address: {
            street: data.street,
            building: data.building,
            city: data.city,
            state: data.state,
            country: data.country,
            pincode: data.pincode,
          },
        },
        (res) => {
          setAddresses(res);
        },
      );
      setBoolean(false);
    }
  }

  return (
    <>
      <Subheading>Delivery Addresses</Subheading>
