        {boolean && (
          <Modal boolean={boolean} setBoolean={setBoolean}>
            <Subheading>Add Address</Subheading>
            <div className={position.container}>
              <Wrapper>
                <Input
                  name="fname"
                  placeholder="First Name*"
                  value={data.fname}
                  onChange={onChange}
                  errName={err.fname}
                />
                <Input
                  name="lname"
                  placeholder="Last Name*"
                  value={data.lname}
                  onChange={onChange}
                  errName={err.lname}
                />
              </Wrapper>
              <Input
                name="street"
                placeholder="Street Address*"
                value={data.street}
                onChange={onChange}
                errName={err.street}
              />
              <Input
                name="building"
                placeholder="Apt, Suite, Building"
                value={data.building}
                onChange={onChange}
                errName={err.building}
              />
              <Wrapper>
                <Input
                  name="city"
                  placeholder="Town/City*"
                  value={data.city}
                  onChange={onChange}
                  errName={err.city}
                />
                <Input
                  name="pincode"
                  placeholder="Postcode*"
                  value={data.pincode}
                  onChange={onChange}
                  errName={err.pincode}
                />
              </Wrapper>
              <Wrapper>
                <SelectCountry
                  name="country"
                  value={data.country}
                  onChange={onChange}
                  placeholder="Country*"
                  setCountry={setCountry}
                  errName={err.country}
                />
                <SelectState
                  name="state"
                  value={data.state}
                  onChange={onChange}
                  placeholder="State*"
                  country={country}
                  errName={err.state}
                />
              </Wrapper>
              <Input
                name="phone"
                placeholder="Phone Number*"
                value={data.phone}
                onChange={onChange}
                errName={err.phone}
              />
            </div>
            <Button text="Save" onClick={onClick} />
          </Modal>
        )}
      </div>
      <Button text="Add Address" onClick={() => setBoolean(!boolean)} />
    </>
  );
}

function ShopPreferences(props) {
  const [data, setData] = useState({
    size: props.preferences?.size || "",
    gender: props.preferences?.gender || "",
    category: props.preferences?.category || [],
    measure: props.preferences?.measure || "",
    user: props.user._id,
  });
  const position = { container: "*:mb-5" };

  function onChange(e) {
    const { name, value, checked } = e.target;
    if (name === "category") {
      setData((prev) => ({
        ...prev,
        category: checked
          ? [...prev.category, value]
          : prev.category.filter((item) => item !== value),
      }));
    } else setData((prev) => ({ ...prev, [name]: value }));
  }
  function onClick() {
    props.preferences?._id ? updatePreferences(data) : createPreferences(data);
  }

  return (
    <>
      <div className={position.container}>
        <Subheading>Shop Preferences</Subheading>
        <Select
          name="size"
          placeholder="Shoe Size*"
          value={data.size}
          onChange={onChange}
          options={[
            { name: "5", value: 5 },
            { name: "5.5", value: 5.5 },
            { name: "6", value: 6 },
            { name: "6.5", value: 6.5 },
            { name: "7", value: 7 },
            { name: "7.5", value: 7.5 },
            { name: "8", value: 8 },
            { name: "8.5", value: 8.5 },
            { name: "9", value: 9 },
            { name: "9.5", value: 9.5 },
            { name: "10", value: 10 },
            { name: "10.5", value: 10.5 },
            { name: "11", value: 11 },
            { name: "11.5", value: 11.5 },
            { name: "12", value: 12 },
          ]}
        />
        <Subsubheading>Shopping Preferences</Subsubheading>
        <Text>
          <Radio
            name="gender"
            options={["Men's", "Women's"]}
            onChange={onChange}
            checked={data.gender}
          />
        </Text>
        <Subsubheading>Additional Preferences</Subsubheading>
        <Text>
          <Checkbox
            name="category"
            options={["Men's", "Women's", "Girls'", "Boys'"]}
            onChange={onChange}
            checked={data.category}
          />
        </Text>
        <Subsubheading>Unit of Measure</Subsubheading>
        <Text>
          <Radio
            name="measure"
            options={["Metric", "Imperial"]}
            onChange={onChange}
            checked={data.measure}
          />
        </Text>
      </div>
      <Button text="Save" onClick={onClick} />
    </>
  );
}

