import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import Button from "../components/button";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "flex md:gap-10 w-11/12 m-auto",
  };
  const sizes = [
    5, 5.5, 6, 6.5, 7, 7.5, 8, 8.5, 9, 9.5, 10, 10.5, 11, 11.5, 12,
  ];

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, [slug]);

  return (
    <Layout>
      {Object.keys(product).length && (
        <div className={position.container}>
          <Image product={product} />
          <ProductInfo sizes={sizes} product={product} />
        </div>
      )}
    </Layout>
  );
}

export default Product;

function Image(props) {
  const position = { container: "w-3/5 p-4", item: "sticky top-0 p-4" };
  const design = { item: "object-contain bg-gray-100 rounded-xl" };

  return (
    <div className={position.container}>
      <img
        className={`${position.item} ${design.item}`}
        src={props.product.image}
        alt={props.product.slug}
        style={{ height: "80vh" }}
      />
    </div>
  );
}

function ProductInfo(props) {
  const position = {
    container: "w-2/5 p-4",
    name: "pb-1",
    discount: "flex gap-2 pt-5",
    buttons: "flex flex-col gap-3 my-5 *:py-4",
    description: "py-5",
  };
  const design = {
    name: "text-lg md:text-2xl",
    category: "text-gray-500",
    discount: "font-medium text-lg",
    price: "text-gray-400 line-through",
  };
  const items = [
    {
      name: "Shipping & Returns",
      description:
        "Free standard shipping on orders $50+ and free 60-day returns for community members.",
    },
  ];

  return (
    <div className={position.container}>
      <h1 className={`${position.name} ${design.name}`}>
        {props.product.name}
      </h1>
      <h2 className={design.category}>{props.product.category} Shoes</h2>
      <h3 className={`${position.discount} ${design.discount}`}>
        $
        {props.product.price -
          (props.product.price * props.product.discount) / 100}
        <span className={design.price}>${props.product.price}</span>
      </h3>
      <Colors product={props.product} />
      <Size sizes={props.sizes} product={props.product} />
      <div className={position.buttons}>
        <Button text="Add to Bag" />
        <Button text="Favorite" light={true} />
      </div>
      <p className={position.description}>{props.product.description}</p>
      {items.map((item) => (
        <Dropdown name={item.name} description={item.description} />
      ))}
    </div>
  );
}

function Colors(props) {
  const position = {
    container: "flex gap-1 mt-10 pb-8",
    item: "w-20 h-20 p-2",
  };
  const design = {
    item: "border object-contain rounded-md hover:border-gray-900",
  };

  return (
    <div className={position.container}>
      {props.product.colors.map((color) => (
        <img
          key={color}
          className={`bg-${color}-400 ${position.item} ${design.item}`}
          src={props.product.image}
          alt={color}
        />
      ))}
    </div>
  );
}

function Size(props) {
  const position = {
    container: "grid grid-cols-3 gap-2",
    item: "h-11",
  };
  const design = {
    heading: "font-medium pb-3",
    item: "font-light border border-gray-300 text-center rounded-md cursor-pointer hover:border-gray-900",
  };

  return (
    <>
      <h3 className={design.heading}>Select Size (UK)</h3>
      <div className={position.container}>
        {props.sizes.map((item) => (
          <input
            key={item}
            className={`${position.item} ${design.item} ${
              !props.product.size.includes(item) && "bg-gray-100 text-gray-300"
            }`}
            value={item}
            readOnly
          />
        ))}
      </div>
    </>
  );
}

function Dropdown(props) {
  const position = { name: "mb-2", hr: "h-px my-8" };
  const design = {
    name: "font-medium text-lg text-gray-700",
    description: "",
    hr: "bg-gray-200",
  };

  return (
    <>
      <hr className={`${position.hr} ${design.hr}`} />
      <h3 className={`${position.name} ${design.name}`}>{props.name}</h3>
      <p className={design.description}>{props.description}</p>
    </>
  );
}
