function Badge(props) {
  const position = {
    container: "relative",
    item: "absolute top-0 -right-2",
  };
  const design = {
    item: "text-sm bg-gray-200 rounded-3xl px-2 py-1",
  };
  return (
    <div className={position.container}>
      {props.component}
      <p className={`${position.item} ${design.item}`}></p>
    </div>
  );
}

export default Badge;
