import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Layout from "../layout";

function Product() {
  const [product, setProduct] = useState([]);
  const { name } = useParams();

  useEffect(() => {
    axios
      .post("/product", { name: name })
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  }, [id]);

  return (
    <Layout>
      {product &&
        product.map((prod) => (
          <div className="flex mt-10 gap-10 w-4/5 m-auto">
            <div className="flex-1 p-4">
              <img
                className="bg-gray-100 px-5 object-contain border border-transparent rounded-xl"
                style={{ height: "70vh" }}
                src={prod.image}
                alt={prod.name}
              />
            </div>
            <div className="flex-1 p-4 mt-5">
              <h2 className="text-3xl">{prod.name}</h2>
              <p className="font-medium text-lg text-gray-500 pt-2">
                {prod.category} Shoes
              </p>
              <p className="font-medium text-lg pt-4">${prod.price}</p>
            </div>
          </div>
        ))}
    </Layout>
  );
}

export default Product;
