import { useEffect, useState } from "react";
import Layout from "../components/layout/layout";
import Card from "../components/card";
import { getProducts } from "../server";

function Collection(props) {
  const [products, setProducts] = useState([]);
  const position = "flex flex-wrap gap-5 justify-center";

  useEffect(() => {
    getProducts({ category: props.category }, setProducts);
  }, [props.category]);

  return (
    <Layout>
      <Topbar category={props.category} />
      <div className={position}>
        {products &&
          products.map((product) => (
            <Card key={product._id} product={product} />
          ))}
      </div>
    </Layout>
  );
}

function Topbar(props) {
  const position = {
    container: "flex justify-between px-5 py-5",
    wrapper: "flex gap-5",
  };
  const design = {
    heading: "font-medium text-2xl",
    text: "font-light tracking-wide",
  };

  return (
    <div className={position.container}>
      <h1 className={design.heading}>{props.category} Shoes</h1>
      <div className={position.wrapper}>
        <h2 className={design.text}>Show Filters</h2>
        <h2 className={design.text}>Sort By</h2>
      </div>
    </div>
  );
}

export default Collection;
