import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Layout from "../components/layout/layout";
import { getProduct } from "../server";

function Product() {
  const [product, setProduct] = useState({});
  const { slug } = useParams();
  const position = {
    container: "grid grid-cols-2",
    productwrapper: "",
    image: "sticky top-0",
    imagewrapper: "",
  };
  const design = {
    heading: "text-2xl",
    subheading: "text-medium text-gray-500",
    image: "object-contain bg-gray-100 rounded-xl",
  };

  useEffect(() => {
    getProduct({ slug }, setProduct);
  }, []);

  return (
    <Layout>
      <div class="grid grid-cols-3">
        <div class="bg-gray-200 p-4 col-span-2">
          {" "}
          This is the bigger column.
        </div>
        <div class="bg-gray-300 p-4"> This is the smaller column.</div>
      </div>{" "}
    </Layout>
  );
}

export default Product;
