import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Layout from "../layout";

function Product() {
  const [product, setProduct] = useState([]);
  const { slug } = useParams();

  useEffect(() => {
    axios
      .post("/product", { slug: slug })
      .then((res) => setProduct(res.data))
      .catch((err) => console.log(err));
  }, [slug]);

  return (
    <Layout>
      {product &&
        product.map((prod) => (
          <div className="flex mt-10 gap-10 w-4/5 m-auto">
            <div className="w-3/5 p-4">
              <img
                className="w-11/12 m-auto bg-gray-100 px-5 object-contain border border-transparent rounded-xl"
                style={{ height: "70vh" }}
                src={prod.image}
                alt={prod.name}
              />
            </div>
            <div className="w-2/5 p-4 mt-5">
              <h2 className="text-2xl">{prod.name}</h2>
              <p className="text-medium text-gray-500 pt-1">
                {prod.category} Shoes
              </p>
              <p className="font-medium text-lg pt-4">${prod.price}</p>
              <div className="flex justify-between mt-20 mb-2">
                <p className="font-medium">Select Size</p>
                <p className="text-gray-500">Size Guide</p>
              </div>
              {[0, 2.5, 5].map((increment, index) => (
                <div
                  key={index}
                  className="flex gap-1 text-center *:p-3 *:w-1/5 *:border *:border-gray-200 *:rounded-md *:mt-1"
                >
                  <p className="hover:border-gray-700">{5 + increment}</p>
                  <p className="hover:border-gray-700">{5.5 + increment}</p>
                  <p className="hover:border-gray-700">{6 + increment}</p>
                  <p className="hover:border-gray-700">{6.5 + increment}</p>
                  <p className="hover:border-gray-700">{7 + increment}</p>
                </div>
              ))}
              <button className="border border-gray-700 rounded-xl w-full py-5">
                Add to Bag
              </button>
              <button>Favorite</button>
            </div>
          </div>
        ))}
    </Layout>
  );
}

export default Product;
