function Filter() {
  const position = {
    container: "w-2/5 *:pb-4",
  };
  return (
    <div className={position.container}>
      <Price />
      <Discount />
      <Color />
      <Size />
    </div>
  );
}

export default Filter;

// Sub-components

function Price() {
  const position = {
    heading: "pb-2",
    inputwrapper: "flex gap-2 items-center pb-2",
  };
  const design = {
    heading: "text-lg font-medium",
  };

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Price</h4>
      {["0-1000", "1000-2000", "2000-3000"].map((item) => (
        <label key={item} className={position.inputwrapper}>
          <input type="radio" name="price" value={item} />
          <span>{item}</span>
        </label>
      ))}
    </div>
  );
}

function Discount() {
  const position = {
    heading: "pb-2",
    inputwrapper: "flex gap-2 items-center pb-2",
  };
  const design = {
    heading: "text-lg font-medium",
  };

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Discount</h4>
      {["0-10", "10-20", "20-30"].map((item) => (
        <label key={item} className={position.inputwrapper}>
          <input type="radio" name="discount" value={item} />
          <span>{item}</span>
        </label>
      ))}
    </div>
  );
}

function Color() {
  const position = {
    heading: "pb-2",
    polkadotwrapper: "grid grid-cols-3 gap-4 w-2/3",
  };
  const design = {
    heading: "text-lg font-medium",
    polkadot:
      "w-5 h-5 rounded-2xl cursor-pointer hover:border hover:border-gray-900",
  };

  return (
    <div>
      <h4 className={`${position.heading} ${design.heading}`}>Color</h4>
      <div className={position.polkadotwrapper}>
        {["green", "blue", "red", "yellow", "orange", "gray"].map((item) => (
          <div className={`${design.polkadot} bg-${item}-400`}></div>
        ))}
      </div>
    </div>
  );
}

function Size() {
  const design = {
    heading: "text-lg font-medium",
  };

  return (
    <div>
      <h4 className={design.heading}>Size</h4>
    </div>
  );
}
