import { useState, useEffect } from "react";
import Layout from "../layout";

function Cart() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const cart = JSON.parse(sessionStorage.getItem("cart"));
    if (cart) {
      const productItems = cart.map((item) => item.product);
      setProducts(productItems);
    }
  }, []);

  return (
    <Layout>
      <div className="w-3/5 pt-10 m-auto">
        <div className="flex">
          <div className="w-7/12">
            <h3 className="font-medium text-lg text-gray-700 mb-5">Bag</h3>
            {products &&
              products.map((product) => (
                <div key={product._id}>
                  <img
                    className="w-24 h-24 object-contain p-4 bg-gray-100"
                    src={product.image}
                    alt={product.name}
                  />
                  <h4>{product.name}</h4>
                </div>
              ))}
          </div>
          <div className="w-5/12">
            <h3 className="font-medium text-lg text-gray-700 mb-5">Summary</h3>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Cart;
