import Layout from "../components/layout/layout";
import Input from "../components/input";

function Checkout() {
  const position = {
    container: "w-2/4 m-auto",
    heading: "pb-8",
    separator: "h-px my-4",
  };
  const design = {
    separator: "bg-gray-200",
    heading: "text-xl text-gray-900 font-medium",
  };

  return (
    <Layout>
      <div className={position.container}>
        {[
          { name: "Delivery", component: <Delivery /> },
          { name: "Shipping", component: <Shipping /> },
          { name: "Payment", component: <Payment /> },
        ].map((item) => (
          <>
            <h3 className={`${position.heading} ${design.heading}`}>
              {item.name}
            </h3>
            {item.component}
            <hr className={`${position.separator} ${design.separator}`} />
          </>
        ))}
      </div>
    </Layout>
  );
}

export default Checkout;

function Delivery() {
  return (
    <div>
      <h1>Delivery</h1>
    </div>
  );
}

function Shipping() {
  return (
    <div>
      <h1>Shipping</h1>
    </div>
  );
}

function Payment() {
  return (
    <div>
      <h1>Payment</h1>
    </div>
  );
}

function Item(props) {
  const design = { heading: "font-mediume", text: "text-sm text-red-500" };

  return (
    <>
      <h4 className={design.heading}>{props.heading}</h4>
      <Input
        name={props.name}
        value={props.value}
        onChange={props.onChange}
        placeholder={props.placeholder}
      />
      {props.errName && <p className={design.text}>{props.errValue}</p>}
    </>
  );
}
